pragma solidity ^0.8.20;
import "hardhat/console.sol";

library LibArray {
    /**
     * @dev Sorts the elements of the array in ascending order using the quicksort algorithm.
     *
     * Requirements:
     *
     * - The array to be sorted must not be empty.
     * - The starting and ending indices must be within the bounds of the array.
     *
     * Modifies:
     *
     * - The array is sorted in ascending order.
     *
     * Note:
     *
     * - This function uses the in-place quicksort algorithm, which has an average-case complexity of O(n log n) and a worst-case complexity of O(n^2).
     */
    function quickSort(uint256[] memory arr, int256 left, int256 right) internal view {
        int256 i = left;
        int256 j = right;
        if (i == j) return;
        uint256 pivot = arr[uint256(left + (right - left) / 2)];
        while (i <= j) {
            while (arr[uint256(i)] > pivot) ++i;
            while (pivot > arr[uint256(j)]) j--;
            if (i <= j) {
                (arr[uint256(i)], arr[uint256(j)]) = (arr[uint256(j)], arr[uint256(i)]);
                ++i;
                j--;
            }
        }
        if (left < j) quickSort(arr, left, j);
        if (i < right) quickSort(arr, i, right);
    }
}
","keccak256":"0x5afcd379801f32d7bc5e740b2b06a644454f1c347d65d8142d8543ff67197e95","license":"MIT"},"src/libraries/LibCoinVending.sol":{"content":"// SPDX-License-Identifier: MIT
// Author: Tim Pechersky <@Peersky>

pragma solidity ^0.8.20;

import {MockERC20} from "../mocks/MockERC20.sol";
import {ERC1155Burnable} from "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";

/**
 * @dev This library is used to simulate the vending machine coin acceptor state machine that:
 *  - Supports large number of positions; Each represents requirements to access different goods of the virtual vending machine.
 *  - Accepts multiple assets of following types: Native (Eth), ERC20, ERC721, and ERC1155 tokens that can be stacked together.
 *  - Allows for each individual asset action promise can be one of following:
 *      - Lock: The asset is locked in the acceptor with promise that asset will be returned to the sender at release funds time.
 *      - Bet: The asset is locked in the acceptor with promise that asset will be awarded to beneficiary at release funds time.
 *      - Pay: The asset is locked in the acceptor with promise that asset will be paid to payee at release funds time.
 *      - Burn: The asset is locked in the acceptor with promise that asset will be destroyed at release funds time.
 *  - Maintains each position balance, hence allowing multiple participants to line up for the same position.
 *  - Allows three actions:
 *      - Fund position with assets
 *      - Refund assets to user
 *      - Consume assets and provide goods to user
 *      - Consuming asset might take a form of
 *      - Transferring assets to payee
 *      - Burning assets
 *      - Awarding beneficiary with assets
 *      - Returning locked assets back to sender
 *
 * This library DOES enforces that any position can only be refunded or processed only within amount funded boundaries
 * This library DOES NOT store the addresses of senders, nor beneficiaries, nor payees.
 * This is to be stored within implementation contract.
 *
 *
 *  !!!!! IMPORTANT !!!!!
 * This library does NOT invocates reentrancy guards. It is implementation contract's responsibility to enforce reentrancy guards.
 * Reentrancy guards MUST be implemented in an implementing contract.
 *
 *  Usage:
 *
 *  0. Configure position via configure(...)
 *  1. fund position with assets via fund(...)
 *  2. release or refund assets via release(...) or refund(...)
 *  3. repeat steps 1 and 2 as needed.
 *  Position can be reconfigured at any time when it's effective balance is zero: `timesFunded - timesRefund - timesReleased = 0`
 *
 *
 * Test state:
 * This library most functionality has been tested: see ../tests/LibCoinVending.ts and ../tests/report.md for details.
 *
 * ERC721 token is checked only for "HAVE" condition since putting requirements on non fungible token id yet to be resolved.
 * (see ERC721 section in the code below)
 *
 * This library has not been yet audited
 *
 */
library LibCoinVending {
    struct Condition {
        mapping(ContractTypes => mapping(address => mapping(uint256 => ContractCondition))) contracts;
        NumericCondition ethValues;
        uint256 timesRefunded;
        uint256 timesReleased;
        uint256 timesFunded;
        ContractTypes[] contractTypes;
        address[] contractAddresses;
        uint256[] contractIds;
        bool _isConfigured;
    }
    enum RequirementTypes {
        HAVE,
        LOCK,
        BURN,
        BET,
        PAY
    }

    struct TransactionProperties {
        bytes data;
        uint256 amount;
    }
    struct ContractCondition {
        TransactionProperties have;
        TransactionProperties lock;
        TransactionProperties burn;
        TransactionProperties pay;
        TransactionProperties bet;
    }

    struct NumericCondition {
        uint256 have;
        uint256 lock;
        uint256 burn;
        uint256 pay;
        uint256 bet;
    }

    enum TransferTypes {
        FUND,
        REFUND,
        RELEASE
    }

    struct ConditionReturn {
        NumericCondition ethValues;
        uint256 timesRefunded;
        uint256 timesReleased;
        uint256 timesFunded;
        address[] contractAddresses;
        uint256[] contractIds;
        ContractTypes[] contractTypes;
        bool _isConfigured;
    }

    struct configSmartRequirement {
        address contractAddress;
        uint256 contractId;
        ContractTypes contractType;
        ContractCondition contractRequirement;
    }

    struct ConfigPosition {
        NumericCondition ethValues;
        configSmartRequirement[] contracts;
    }

    struct LibCoinVendingStorage {
        mapping(bytes32 => Condition) positions;
        address beneficiary;
    }

    enum ContractTypes {
        ERC20,
        ERC1155,
        ERC721
    }

    bytes32 constant COIN_VENDING_STORAGE_POSITION = keccak256("coin.vending.storage.position");

    function coinVendingPosition(bytes32 position) internal view returns (Condition storage) {
        return coinVendingStorage().positions[keccak256(abi.encode(position))];
    }

    function coinVendingStorage() internal pure returns (LibCoinVendingStorage storage es) {
        bytes32 position = COIN_VENDING_STORAGE_POSITION;
        assembly {
            es.slot := position
        }
    }

    /**
     * @dev Transfers a specified amount of tokens from one address to another, or burns them if the destination address is zero.
     *
     * Requirements:
     *
     * - The `value` must be non-zero.
     * - The `from` address must have a sufficient token balance.
     * - If the `from` address is not this contract, it must have approved this contract to transfer tokens on its behalf.
     *
     * Modifies:
     *
     * - The token balances of the `from` and `to` addresses, or the total supply of tokens if `to` is the zero address.
     */
    function transferFromAny(address erc20Addr, address from, address to, uint256 value) private {
        MockERC20 token = MockERC20(erc20Addr);
        if (value != 0) {
            if (from == address(this)) {
                if (to != address(0)) {
                    token.transfer(to, value);
                } else {
                    token.burn(value);
                }
            } else {
                if (to != address(0)) {
                    token.transferFrom(from, to, value);
                } else {
                    token.transferFrom(from, address(this), value);
                    token.burn(value);
                }
            }
        }
    }

    /**
     * @dev Fulfills the ERC20 token transfer according to the specified rules.
     *
     * Requirements:
     *
     * - The `from` address must have a sufficient token balance.
     * - If the `from` address is not this contract, it must have approved this contract to transfer tokens on its behalf.
     *
     * Modifies:
     *
     * - The token balances of the `from` and `to` addresses.
     */
    function fulfillERC20(
        address erc20Addr,
        ContractCondition storage tokenReq,
        address from,
        address payee,
        address beneficiary,
        address burnAddress,
        address lockAddress
    ) private {
        transferFromAny(erc20Addr, from, lockAddress, tokenReq.lock.amount);
        transferFromAny(erc20Addr, from, burnAddress, tokenReq.burn.amount);
        transferFromAny(erc20Addr, from, payee, tokenReq.pay.amount);
        transferFromAny(erc20Addr, from, beneficiary, tokenReq.bet.amount);
        MockERC20 token = MockERC20(erc20Addr);
        uint256 value = tokenReq.have.amount;
        if (value != 0 && from != address(this)) {
            require(token.balanceOf(from) >= value, "Not enough erc20 tokens");
        }
    }

    /**
     * @dev Fulfills the ERC721 token transfer according to the specified rules.
     *
     * Requirements:
     *
     * - The `from` address must own the token.
     * - If the `from` address is not this contract, it must have approved this contract to transfer the token on its behalf.
     *
     * Modifies:
     *
     * - The token ownership from the `from` address to the `to` address.
     *
     * Notes:
     *
     * Due to non fungible nature it's an open question how to implement this method correctly for lock/burn/pay/bet cases.
     * In this library I assume that requirements are for multiple members, hence it makes no sense to put requirement on particular tokenId for ERC721.
     * I think best approach would be to split in to two methods:
     *  1. fulfillERC72Balance: Treats tokens as fungible - requires one to lock/burn/pay/bet ANY token id, but in total should be equal to desired value.
     *  2. fulfillERC721Ids: Requires one to lock/burn/pay/bet specific token id. (useful when requirements are unique per applicant).
     * fulfillERC72Balance is easy. fulfillERC721Ids brings up a question of how to select those ID's(since must specify for ERC721 contract on transfer method).
     *  Two possible solutions:
     *  1: modify fund() method to accept array of address+id pairs of NFT's and parse trough it. computationally inefficient.
     *  2: implement onERC721Received such that there is NFT vault in the contract, later fill funding position from that vault. That way applicant could pre-send NFT's to the contract and calling fund later would pull those out from the vault.

     */
    function fulfillERC72Balance(address erc721addr, ContractCondition storage tokenReq, address from) private view {
        ERC721 token = ERC721(erc721addr);

        require(
            tokenReq.lock.amount == 0 &&
                tokenReq.burn.amount == 0 &&
                tokenReq.pay.amount == 0 &&
                tokenReq.bet.amount == 0,
            "ERC721 transfers not supported"
        );
        if (tokenReq.have.amount != 0 && from != address(this)) {
            uint256 balance = token.balanceOf(from);
            require(balance >= tokenReq.have.amount, "Not enough ERC721 balance");
        }
    }

    /**
     * @dev Fulfills the ERC1155 token transfer according to the specified rules.
     *
     * Requirements:
     *
     * - The `from` address must own the token.
     * - If the `from` address is not this contract, it must have approved this contract to transfer the token on its behalf.
     *
     * Modifies:
     *
     * - The token ownership from the `from` address to the `to` address.
     */
    function fulfillERC1155(
        address erc1155addr,
        uint256 id,
        ContractCondition storage tokenReq,
        address from,
        address payee,
        address beneficiary,
        address burnAddress,
        address lockAddress
    ) private {
        ERC1155Burnable token = ERC1155Burnable(erc1155addr);
        uint256 value = tokenReq.have.amount;
        if (value != 0) {
            uint256 balance = token.balanceOf(from, id);
            require(balance >= value, "ERC1155 balance is not valid");
        }
        value = tokenReq.pay.amount;
        if (value != 0) {
            token.safeTransferFrom(from, payee, id, value, tokenReq.pay.data);
        }
        value = tokenReq.bet.amount;
        if (value != 0) {
            token.safeTransferFrom(from, beneficiary, id, value, tokenReq.bet.data);
        }
        value = tokenReq.burn.amount;
        if (value != 0) {
            if (burnAddress == address(0)) {
                token.burn(from, id, value);
            } else {
                token.safeTransferFrom(from, burnAddress, id, value, tokenReq.burn.data);
            }
        }
        value = tokenReq.lock.amount;
        if (value != 0) {
            token.safeTransferFrom(from, lockAddress, id, value, tokenReq.lock.data);
        }
    }

    /**
     * @dev Fulfills the conditions of a position.
     *
     * Requirements:
     *
     * - If `from` is not this contract, the sent value must be greater than or equal to the sum of the locked, paid, bet, and burned values.
     *
     * Modifies:
     *
     * - Transfers the specified amounts of Ether to the lock, payee, beneficiary, and burn addresses.
     */
    function fulfill(
        Condition storage position,
        address from,
        address payee,
        address beneficiary,
        address burnAddress,
        address lockAddress
    ) private {
        if (from == address(this)) {
            if (position.ethValues.lock != 0) {
                payable(lockAddress).transfer(position.ethValues.lock);
            }
            if (position.ethValues.pay != 0) {
                payable(payee).transfer(position.ethValues.pay);
            }
            if (position.ethValues.bet != 0) {
                payable(beneficiary).transfer(position.ethValues.bet);
            }
            if (position.ethValues.burn != 0) {
                payable(burnAddress).transfer(position.ethValues.burn);
            }
        } else {
            uint256 VLReq = position.ethValues.lock +
                position.ethValues.pay +
                position.ethValues.bet +
                position.ethValues.burn;
            require(msg.value >= VLReq, "msg.value too low");
        }
        for (uint256 i = 0; i < position.contractAddresses.length; ++i) {
            address contractAddress = position.contractAddresses[i];
            uint256 id = position.contractIds[i];
            ContractTypes contractType = position.contractTypes[i];
            ContractCondition storage requirement = position.contracts[contractType][contractAddress][id];
            if (contractType == ContractTypes.ERC20) {
                fulfillERC20(contractAddress, requirement, from, payee, beneficiary, burnAddress, lockAddress);
            } else if (contractType == ContractTypes.ERC721) {
                fulfillERC72Balance(
                    contractAddress,
                    // id,
                    requirement,
                    from
                    // payee,
                    // beneficiary,
                    // burnAddress,
                    // lockAddress
                );
            } else if (contractType == ContractTypes.ERC1155) {
                fulfillERC1155(contractAddress, id, requirement, from, payee, beneficiary, burnAddress, lockAddress);
            }
        }
    }

    /**
     * @dev Refunds the balance of a condition to the specified address.
     *
     * `reqPos` The storage reference to the condition.
     * `to` The address to refund the balance to.
     *
     * Requirements:
     *
     * - The sum of `timesRefunded` and `timesReleased` for the condition must be less than `timesFunded`.
     *
     * Modifies:
     *
     * - Transfers the remaining balance of the condition to the `to` address.
     * - Increments the `timesRefunded` counter for the condition.
     */
    function _refund(Condition storage reqPos, address to) private {
        require((reqPos.timesRefunded + reqPos.timesReleased) < reqPos.timesFunded, "Not enough balance to refund");
        fulfill(reqPos, address(this), to, to, to, to);
        reqPos.timesRefunded += 1;
    }

    /**
     * @dev Returns all position requirements back to founder. `position` is the identifier of the condition. `to` is the address to refund the balance to.
     *
     * Requirements:
     *
     * - The sum of `timesRefunded` and `timesReleased` for the condition must be less than `timesFunded`.
     *
     * Modifies:
     *
     * - Transfers the remaining balance of the condition to the `to` address.
     * - Increments the `timesRefunded` counter for the condition.
     */
    function refund(bytes32 position, address to) internal {
        Condition storage reqPos = coinVendingPosition(position);
        _refund(reqPos, to);
    }

    /**
     * @dev Returns all position requirements back to multiple founders. `position` is the identifier of the condition. `returnAddresses` is an array of addresses to refund the balance to.
     *
     * Requirements:
     *
     * - The sum of `timesRefunded` and `timesReleased` for the condition must be less than `timesFunded`.
     *
     * Modifies:
     *
     * - Transfers the remaining balance of the condition to each address in `returnAddresses`.
     * - Increments the `timesRefunded` counter for the condition for each address in `returnAddresses`.
     */
    function batchRefund(bytes32 position, address[] memory returnAddresses) internal {
        Condition storage reqPos = coinVendingPosition(position);
        for (uint256 i = 0; i < returnAddresses.length; ++i) {
            _refund(reqPos, returnAddresses[i]);
        }
    }

    function _release(Condition storage reqPos, address payee, address beneficiary, address returnAddress) private {
        require((reqPos.timesRefunded + reqPos.timesReleased) < reqPos.timesFunded, "Not enough balance to release");
        fulfill(reqPos, address(this), payee, beneficiary, address(0), returnAddress);
        reqPos.timesReleased += 1;
    }

    /**
     * @dev Releases the funds from a coin vending position to the specified addresses. `position` is the identifier of the condition. `payee`, `beneficiary`, and `returnAddress` are the addresses to release the funds to.
     *
     * Requirements:
     *
     * - The sum of `timesRefunded` and `timesReleased` for the condition must be less than `timesFunded`.
     *
     * Modifies:
     *
     * - Transfers the remaining balance of the condition to the `payee`, `beneficiary`, and `returnAddress`.
     * - Increments the `timesReleased` counter for the condition.
     */
    function release(bytes32 position, address payee, address beneficiary, address returnAddress) internal {
        Condition storage reqPos = coinVendingPosition(position);
        _release(reqPos, payee, beneficiary, returnAddress);
    }

    /**
     * @dev Releases the funds from a coin vending position to multiple return addresses. `position` is the identifier of the condition. `payee`, `beneficiary`, and `returnAddresses` are the addresses to release the funds to.
     *
     * Requirements:
     *
     * - The sum of `timesRefunded` and `timesReleased` for the condition must be less than `timesFunded`.
     *
     * Modifies:
     *
     * - Transfers the remaining balance of the condition to the `payee`, `beneficiary`, and each address in `returnAddresses`.
     * - Increments the `timesReleased` counter for the condition for each address in `returnAddresses`.
     */
    function batchRelease(
        bytes32 position,
        address payee,
        address beneficiary,
        address[] memory returnAddresses
    ) internal {
        Condition storage reqPos = coinVendingPosition(position);
        for (uint256 i = 0; i < returnAddresses.length; ++i) {
            {
                _release(reqPos, payee, beneficiary, returnAddresses[i]);
            }
        }
    }

    function _fund(Condition storage reqPos, address founder) private {
        require(reqPos._isConfigured, "Position does not exist");
        fulfill(reqPos, founder, address(this), address(this), address(this), address(this));
        reqPos.timesFunded += 1;
    }

    /**
     * @dev Funds the position by `msg.sender`. `position` is the identifier of the condition.
     *
     * Requirements:
     *
     * - The condition must be configured.
     *
     * Modifies:
     *
     * - Transfers the funds from `msg.sender` to this contract.
     * - Increments the `timesFunded` counter for the condition.
     */
    function fund(bytes32 position) internal {
        Condition storage reqPos = coinVendingPosition(position);
        _fund(reqPos, msg.sender);
    }

    /**
     * @dev Configures the position. `position` is the identifier of the condition. `configuration` is the new configuration for the condition.
     *
     * Requirements:
     *
     * - The condition must not have a positive balance.
     *
     * Modifies:
     *
     * - Sets the configuration of the condition to `configuration`.
     */
    function configure(bytes32 position, ConfigPosition memory configuration) internal {
        Condition storage mustDo = coinVendingPosition(position);
        require(
            mustDo.timesFunded == 0 || (mustDo.timesFunded == (mustDo.timesRefunded + mustDo.timesReleased)),
            "Cannot mutate position with currently positive balance"
        );
        mustDo.ethValues = configuration.ethValues;
        delete mustDo.contractAddresses;
        delete mustDo.contractIds;
        delete mustDo.contractTypes;
        for (uint256 i = 0; i < configuration.contracts.length; ++i) {
            mustDo.contractAddresses.push(configuration.contracts[i].contractAddress);
            mustDo.contractIds.push(configuration.contracts[i].contractId);
            mustDo.contractTypes.push(configuration.contracts[i].contractType);
            mustDo.contracts[configuration.contracts[i].contractType][configuration.contracts[i].contractAddress][
                configuration.contracts[i].contractId
            ] = configuration.contracts[i].contractRequirement;
        }
        mustDo._isConfigured = true;
    }

    /**
     * @dev Returns the condition associated with the given position. `position` is the identifier of the condition.
     *
     * Returns:
     *
     * - The condition associated with `position`.
     */
    function getPosition(bytes32 position) internal view returns (ConditionReturn memory) {
        Condition storage pos = coinVendingPosition(position);
        ConditionReturn memory ret;
        ret.ethValues = pos.ethValues;
        ret.timesFunded = pos.timesFunded;
        ret.timesRefunded = pos.timesRefunded;
        ret.timesReleased = pos.timesReleased;
        ret._isConfigured = pos._isConfigured;
        ret.contractAddresses = pos.contractAddresses;
        ret.contractIds = pos.contractIds;
        ret.contractTypes = pos.contractTypes;
        return ret;
    }

    /**
     * @dev Returns the contract condition associated with the given position, contract address, contract ID, and contract type. `position` is the identifier of the condition. `contractAddress` is the address of the contract. `contractId` is the ID of the contract. `contractType` is the type of the contract.
     *
     * Returns:
     *
     * - The contract condition associated with `position`, `contractAddress`, `contractId`, and `contractType`.
     */
    function getPositionByContract(
        bytes32 position,
        address contractAddress,
        uint256 contractId,
        ContractTypes contractType
    ) internal view returns (ContractCondition memory) {
        Condition storage pos = coinVendingPosition(position);
        return pos.contracts[contractType][contractAddress][contractId];
    }
}
","keccak256":"0x8785852c824a7c666fdaaa7ab8908b1874e804e9230fbbe7427aff2f99fc1482","license":"MIT"},"src/libraries/LibQuadraticVoting.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
error quadraticVotingError(string parameter, uint256 arg, uint256 arg2);

/**
 * @title LibQuadraticVoting
 * @dev A library for quadratic voting calculations.
 */
library LibQuadraticVoting {
    struct qVotingStruct {
        uint256 voteCredits;
        uint256 maxQuadraticPoints;
        uint256 minQuadraticPositions;
    }

    /**
     * @dev Pre-computes the values for quadratic voting. `voteCredits` is the total number of vote credits. `minExpectedVoteItems` is the minimum expected number of vote items.
     *
     * Returns:
     *
     * - A `qVotingStruct` containing the precomputed values.
     */
    function precomputeValues(
        uint256 voteCredits,
        uint256 minExpectedVoteItems
    ) internal pure returns (qVotingStruct memory) {
        qVotingStruct memory q;

        q.maxQuadraticPoints = Math.sqrt(voteCredits);

        // This block finds how many vote positions are needed to distribute all quadratic vote points.
        uint256 iterator = 0;
        uint256 accumulator = 0;
        do {
            accumulator += (q.maxQuadraticPoints - iterator) ** 2;
            iterator++;
        } while (accumulator < voteCredits);
        // This enforces requirement that all vote credits can indeed be spent (no leftovers)
        if (accumulator != voteCredits) require(false, "quadraticVotingError: voteCredits must be i^2 series"); //revert quadraticVotingError("voteCredits must be i^2 series", accumulator, voteCredits);
        q.minQuadraticPositions = iterator;
        // In order to spend all vote credits there must be at least minQuadraticPositions+1 (because proposer is also a player and cannot vote for himself)
        if (minExpectedVoteItems <= q.minQuadraticPositions)
            require(false, "quadraticVotingError: Minimum Voting positions above min players");
        // revert quadraticVotingError(
        //     "Minimum Voting positions above min players",
        //     q.minQuadraticPositions,
        //     minExpectedVoteItems
        // );
        q.voteCredits = voteCredits;
        return q;
    }

    /**
     * @dev Computes the scores for each proposal by voter preference index. `q` is the precomputed quadratic voting values. `VotersVotes` is a 2D array of votes, where each row corresponds to a voter and each column corresponds to a proposal. `isActive` is an array indicating whether each voter has voted.
     *
     * Returns:
     *
     * - An array of scores for each proposal.
     */
    function tallyVotes(
        qVotingStruct memory q,
        uint256[][] memory tally, // [participant][votedFor]
        bool[] memory isActive
    ) internal pure returns (uint256[] memory) {
        uint256 notVotedGivesEveryone = q.maxQuadraticPoints;
        uint256[] memory scores = new uint256[](tally.length);
        uint256[] memory creditsUsed = new uint256[](tally.length);

        for (uint256 participant = 0; participant < tally.length; participant++) {
            //For each proposal
            // console.log("New tally iter");
            uint256[] memory votedFor = tally[participant];
            for (uint256 candidate = 0; candidate < tally.length; candidate++) {
                // For each potential voter
                if (!isActive[participant] && isActive[candidate]) {
                    // Check if participant wasn't voting
                    scores[candidate] += notVotedGivesEveryone; // Gives benefits to everyone but himself
                    creditsUsed[participant] = q.voteCredits;
                } else {
                    //If participant voted
                    scores[candidate] += votedFor[candidate];
                    creditsUsed[participant] += votedFor[candidate] ** 2;
                }
            }
            require(
                creditsUsed[participant] <= q.voteCredits,
                quadraticVotingError("Quadratic: vote credits overrun", q.voteCredits, creditsUsed[participant])
            );
        }
        return scores;
    }
}
","keccak256":"0x75ab448876b3cbad2b9f00b539b82270a785fd13b3450a04194ecbc8a7831a0c","license":"MIT"},"src/libraries/LibRankify.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;
import {LibTBG} from "../libraries/LibTurnBasedGame.sol";
import {IRankifyInstance} from "../interfaces/IRankifyInstance.sol";
import {IRankToken} from "../interfaces/IRankToken.sol";
import "../tokens/Rankify.sol";
import {LibQuadraticVoting} from "./LibQuadraticVoting.sol";
import "hardhat/console.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {SignedMath} from "@openzeppelin/contracts/utils/math/SignedMath.sol";
import {IErrors} from "../interfaces/IErrors.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
/**
 * @title LibRankify
 * @dev Core library for the Rankify protocol that handles game state management, voting, and player interactions
 * @author Peeramid Labs, 2024
 */
library LibRankify {
    using LibTBG for LibTBG.Instance;
    using LibTBG for uint256;
    using LibTBG for LibTBG.Settings;
    using LibTBG for LibTBG.State;
    using LibQuadraticVoting for LibQuadraticVoting.qVotingStruct;

    /**
     * @dev Main state structure for a Rankify instance
     * @param numGames Total number of games created in this instance
     * @param contractInitialized Whether the contract has been properly initialized
     * @param commonParams Common parameters shared across all games in this instance
     */
    struct InstanceState {
        uint256 numGames;
        bool contractInitialized;
        CommonParams commonParams;
    }

    /**
     * @dev Common parameters shared across all games in a Rankify instance
     * @param principalCost Base cost for creating a game
     * @param principalTimeConstant Time constant used for game duration calculations
     * @param gamePaymentToken Address of the token used for game payments
     * @param rankTokenAddress Address of the rank token contract
     * @param beneficiary Address that receives a portion of game fees
     * @param minimumParticipantsInCircle Minimum number of participants required to join a game
     */
    struct CommonParams {
        uint256 principalCost;
        uint96 principalTimeConstant;
        address gamePaymentToken;
        address rankTokenAddress;
        address beneficiary;
        uint256 minimumParticipantsInCircle;
        address derivedToken;
        address proposalIntegrityVerifier;
        address poseidon5;
        address poseidon6;
        address poseidon2;
    }

    /**
     * @dev Comprehensive state structure for an individual game
     * @param rank Required rank level for participation
     * @param minGameTime Minimum duration the game must run
     * @param createdBy Address of the game creator
     * @param numOngoingProposals Number of active proposals
     * @param numPrevProposals Number of completed proposals
     * @param numCommitments Number of vote commitments received
     * @param numVotesThisTurn Vote count in current turn
     * @param numVotesPrevTurn Vote count from previous turn
     * @param voting Quadratic voting state for this game
     */
    struct GameState {
        uint256 rank;
        string metadata;
        uint256 minGameTime;
        address createdBy;
        uint256 numOngoingProposals;
        uint256 numPrevProposals;
        uint256 numCommitments;
        uint256 numVotesThisTurn;
        uint256 numVotesPrevTurn;
        uint256 permutationCommitment;
        LibQuadraticVoting.qVotingStruct voting;
        mapping(uint256 => string) ongoingProposals; //Previous Turn Proposals (These are being voted on)
        mapping(address => uint256) proposalCommitment;
        mapping(address => bytes32) ballotHashes;
        mapping(address => bool) playerVoted;
        address winner;
    }

    /**
     * @dev Compares two strings for equality. `a` and `b` are the strings to compare.
     *
     * Returns:
     *
     * - `true` if the strings are equal, `false` otherwise.
     */
    function compareStrings(string memory a, string memory b) public pure returns (bool) {
        return (keccak256(abi.encodePacked((a))) == keccak256(abi.encodePacked((b))));
    }

    /**
     * @dev Returns the game storage for the given game ID. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - The game storage for `gameId`.
     */
    function getGameState(uint256 gameId) public view returns (GameState storage game) {
        bytes32 position = LibTBG.getGameDataStorage(gameId);
        assembly {
            game.slot := position
        }
    }

    /**
     * @dev Returns the Rankify InstanceSettings storage.
     *
     * Returns:
     *
     * - The instanceState storage.
     */
    function instanceState() internal pure returns (InstanceState storage contractState) {
        bytes32 position = LibTBG.getDataStorage();
        assembly {
            contractState.slot := position
        }
    }

    bytes32 public constant _PROPOSAL_PROOF_TYPEHASH =
        keccak256("signProposalByGM(uint256 gameId,uint256 turn,bytes32 proposalNHash,string encryptedProposal)");
    bytes32 public constant _VOTE_PROOF_TYPEHASH =
        keccak256("signVote(uint256 vote1,uint256 vote2,uint256 vote3,uint256 gameId,uint256 turn,bytes32 salt)");
    bytes32 public constant _VOTE_SUBMIT_PROOF_TYPEHASH =
        keccak256("publicSignVote(uint256 gameId,uint256 turn,bytes32 vote1,bytes32 vote2,bytes32 vote3)");

    /**
     * @dev Ensures that the contract is initialized.
     *
     * Requirements:
     *
     * - The contract must be initialized.
     */
    function enforceIsInitialized() public view {
        InstanceState storage settings = instanceState();
        require(settings.contractInitialized, "onlyInitialized");
    }

    /**
     * @dev Ensures that the game with the given ID exists. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     */
    function enforceGameExists(uint256 gameId) public view {
        enforceIsInitialized();
        require(gameId.gameExists(), "game not found");
    }

    struct NewGameParams {
        uint256 gameId;
        uint256 gameRank;
        address creator;
        uint256 minPlayerCnt;
        uint256 maxPlayerCnt;
        uint256 voteCredits;
        address gameMaster;
        uint96 nTurns;
        uint128 minGameTime;
        uint128 timePerTurn;
        uint128 timeToJoin;
        string metadata;
        // ToDo: It must list gameKey for Game master and game master signature, committing to serve the game
    }

    function getGamePrice(uint128 minGameTime, CommonParams memory commonParams) public pure returns (uint256) {
        return
            Math.mulDiv(
                uint256(commonParams.principalCost),
                uint256(commonParams.principalTimeConstant),
                uint256(minGameTime)
            );
    }

    /**
     * @dev Creates a new game with the given parameters. `gameId` is the ID of the new game. `gameMaster` is the address of the game master. `gameRank` is the rank of the game. `creator` is the address of the creator of the game.
     *
     * Requirements:
     *
     * - The game with `gameId` must not already exist.
     * - `gameRank` must not be 0.
     * - If the game price is not 0, the `creator` must have approved this contract to transfer the game price amount of the game payment token on their behalf.
     *
     * Modifies:
     *
     * - Creates a new game with `gameId`.
     * - Transfers the game price amount of the game payment token from `creator` to this contract.
     * - Sets the payments balance of the game to the game price.
     * - Sets the creator of the game to `creator`.
     * - Increments the number of games.
     * - Sets the rank of the game to `gameRank`.
     * - Mints new rank tokens.
     */
    function newGame(NewGameParams memory params) public {
        // address signer = ECDSA.recover(digest, gameMasterSignature);
        //TODO: add this back in start game to verify commitment from game master
        // require(
        //     params.gameMaster == signer,
        //     IErrors.invalidECDSARecoverSigner(digest, "LibRankify::newGame->invalid signature")
        // );

        enforceIsInitialized();
        CommonParams storage commonParams = instanceState().commonParams;

        require(params.minGameTime > 0, "LibRankify::newGame->Min game time zero");
        require(params.nTurns > 1, IRankifyInstance.invalidTurnCount(params.nTurns));

        LibTBG.Settings memory newSettings = LibTBG.Settings({
            timePerTurn: params.timePerTurn,
            maxPlayerCnt: params.maxPlayerCnt,
            minPlayerCnt: params.minPlayerCnt,
            timeToJoin: params.timeToJoin,
            maxTurns: params.nTurns,
            voteCredits: params.voteCredits,
            gameMaster: params.gameMaster,
            implementationStoragePointer: bytes32(0)
        });

        InstanceState storage state = instanceState();

        params.gameId.createGame(newSettings); // This will enforce game does not exist yet
        GameState storage game = getGameState(params.gameId);
        game.voting = LibQuadraticVoting.precomputeValues(params.voteCredits, params.maxPlayerCnt);
        game.metadata = params.metadata;
        require(
            SignedMath.abs(int256(uint256(params.minGameTime)) - int256(uint256(commonParams.principalTimeConstant))) <
                uint256(commonParams.principalTimeConstant) * 2 ** 16,
            "Min game time out of bounds"
        );
        require(commonParams.minimumParticipantsInCircle <= params.minPlayerCnt, "Min player count too low");
        uint256 principalGamePrice = getGamePrice(params.minGameTime, commonParams);
        uint256 burnAmount = Math.mulDiv(principalGamePrice, 9, 10);
        uint256 daoAmount = principalGamePrice - burnAmount;
        address beneficiary = commonParams.beneficiary;

        Rankify(commonParams.gamePaymentToken).burnFrom(params.creator, burnAmount);
        Rankify(commonParams.gamePaymentToken).transferFrom(params.creator, beneficiary, daoAmount);

        require(params.gameRank != 0, IRankifyInstance.RankNotSpecified());

        game.createdBy = params.creator;
        state.numGames += 1;
        game.rank = params.gameRank;
        game.minGameTime = params.minGameTime;

        IRankToken rankTokenContract = IRankToken(state.commonParams.rankTokenAddress);
        rankTokenContract.mint(address(this), 1, params.gameRank + 1, "");
    }

    /**
     * @dev Ensures that the candidate is the creator of the game with the given ID. `gameId` is the ID of the game. `candidate` is the address of the candidate.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     * - `candidate` must be the creator of the game.
     */
    function enforceIsGameCreator(uint256 gameId, address candidate) public view {
        enforceGameExists(gameId);
        GameState storage game = getGameState(gameId);
        require(game.createdBy == candidate, "Only game creator");
    }

    /**
     * @dev Ensures that the candidate is the game master of the game with the given ID. `gameId` is the ID of the game. `candidate` is the address of the candidate.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     * - `candidate` must be the game master of the game.
     */
    function enforceIsGM(uint256 gameId, address candidate) public view {
        enforceGameExists(gameId);
        require(gameId.getGM() == candidate, "Only game master");
    }

    /**
     * @dev Locks the rank token of the player. `player` is the address of the player. `gameRank` is the rank of the game. `rankTokenAddress` is the address of the rank token contract.
     *
     * Requirements:
     *
     * - `RankTokenAddress` must support `IRankToken` interface
     *
     * Modifies:
     *
     * - Locks `gameRank` rank of `player` in the rank token contract.
     */
    function _fulfillRankRq(address player, uint256 gameRank, address rankTokenAddress) private {
        IRankToken rankToken = IRankToken(rankTokenAddress);
        rankToken.lock(player, gameRank, 1);
    }

    /**
     * @dev Allows a player to join a game. `gameId` is the ID of the game. `player` is the address of the player.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     * - If the join game price is not 0, the `player` must have approved this contract to transfer the join game price amount of the game payment token on their behalf.
     *
     * Modifies:
     *
     * - Transfers the join game price amount of the game payment token from `player` to this contract.
     * - Increases the payments balance of the game by the join game price.
     * - Adds `player` to the game.
     */
    function joinGame(uint256 gameId, address player, bytes memory gameMasterSignature, bytes32 digest) public {
        enforceGameExists(gameId);
        fulfillRankRq(gameId, player);
        gameId.addPlayer(player);
        address signer = ECDSA.recover(digest, gameMasterSignature);
        require(
            gameId.getGM() == signer,
            IErrors.invalidECDSARecoverSigner(digest, "LibRankify::joinGame->invalid signature")
        );
    }

    /**
     * @dev Closes the game with the given ID and transfers the game's balance to the beneficiary. `gameId` is the ID of the game. `beneficiary` is the address to transfer the game's balance to. `playersGameEndedCallback` is a callback function to call for each player when the game ends.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     *
     * Modifies:
     *
     * - Emits rank rewards for the game.
     * - Removes and unlocks each player from the game.
     * - Calls `playersGameEndedCallback` for each player.
     * - Transfers the game's balance to `beneficiary`.
     *
     * Returns:
     *
     * - The final scores of the game.
     */
    function closeGame(
        uint256 gameId,
        function(uint256, address) playersGameEndedCallback
    ) internal returns (address[] memory, uint256[] memory) {
        enforceGameExists(gameId);

        // Get game state and check minimum time
        GameState storage game = getGameState(gameId);
        LibTBG.State storage tbgState = gameId._getState();
        require(
            block.timestamp - tbgState.startedAt >= game.minGameTime,
            "Game duration less than minimum required time"
        );

        (, uint256[] memory finalScores) = gameId.getScores();
        address[] memory players = gameId.getPlayers();
        for (uint256 i = 0; i < players.length; ++i) {
            removeAndUnlockPlayer(gameId, players[i]);
            playersGameEndedCallback(gameId, players[i]);
        }
        emitRankRewards(gameId, gameId.getLeaderBoard());
        return (players, finalScores);
    }

    /**
     * @dev Allows a player to quit a game. `gameId` is the ID of the game. `player` is the address of the player. `slash` is a boolean indicating whether to slash the player's payment refund. `onPlayerLeftCallback` is a callback function to call when the player leaves.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     *
     * Modifies:
     *
     * - If the join game price is not 0, transfers a refund to `player` and decreases the game's payments balance by the refund amount.
     * - Removes and unlocks `player` from the game.
     * - Calls `onPlayerLeftCallback` for `player`.
     */
    function quitGame(uint256 gameId, address player, function(uint256, address) onPlayerLeftCallback) internal {
        removeAndUnlockPlayer(gameId, player); // this will throw if game has started or doesn't exist
        onPlayerLeftCallback(gameId, player);
    }

    /**
     * @dev Cancels the game with the given ID, refunds half of the game's payment to the game creator, and transfers the remaining balance to the beneficiary. `gameId` is the ID of the game. `onPlayerLeftCallback` is a callback function to call for each player when they leave. `beneficiary` is the address to transfer the remaining balance to.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     *
     * Modifies:
     *
     * - Calls `quitGame` for each player in the game.
     * - Transfers half of the game's payment to the game creator.
     * - Decreases the game's payments balance by the refund amount.
     * - Transfers the remaining balance of the game to `beneficiary`.
     * - Deletes the game.
     */ function cancelGame(uint256 gameId, function(uint256, address) onPlayerLeftCallback) internal {
        // Cancel the game for each player
        address[] memory players = gameId.getPlayers();
        for (uint256 i = 0; i < players.length; ++i) {
            quitGame(gameId, players[i], onPlayerLeftCallback); //this will throw if game has started or doesn't exist
        }

        // Delete the game
        gameId.deleteGame();
    }

    /**
     * @dev Fulfills the rank requirement for a player to join a game. `gameId` is the ID of the game. `player` is the address of the player.
     *
     * Modifies:
     *
     * - Locks the rank token(s) of `player` in the rank token contract.
     */
    function fulfillRankRq(uint256 gameId, address player) public {
        InstanceState storage instance = instanceState();
        GameState storage game = getGameState(gameId);
        if (game.rank > 1) {
            _fulfillRankRq(player, game.rank, instance.commonParams.rankTokenAddress);
        }
    }

    /**
     * @dev Emits rank rewards to the top three addresses in the leaderboard. `gameId` is the ID of the game. `leaderboard` is an array of addresses representing the leaderboard sorted in descending order. `rankTokenAddress` is the address of the rank token contract.
     *
     * Modifies:
     *
     * - Transfers rank tokens from this contract to the top three addresses in the leaderboard.
     */
    function emitRankReward(uint256 gameId, address[] memory leaderboard, address rankTokenAddress) private {
        GameState storage game = getGameState(gameId);
        IRankToken rankTokenContract = IRankToken(rankTokenAddress);
        if (game.rank > 1) {
            rankTokenContract.burn(leaderboard[0], game.rank, 1);
        }
        rankTokenContract.safeTransferFrom(address(this), leaderboard[0], game.rank + 1, 1, "");
        game.winner = leaderboard[0];
    }

    /**
     * @dev Emits rank rewards to the top addresses in the leaderboard for each rank in the game. `gameId` is the ID of the game. `leaderboard` is an array of addresses representing the leaderboard.
     *
     * Modifies:
     *
     * - Calls `emitRankReward` for the main rank and each additional rank in the game.
     */
    function emitRankRewards(uint256 gameId, address[] memory leaderboard) public {
        InstanceState storage instance = LibRankify.instanceState();
        emitRankReward(gameId, leaderboard, instance.commonParams.rankTokenAddress);
    }

    /**
     * @dev Releases a rank token for a player with a specific game rank. `player` is the address of the player. `gameRank` is the game rank of the player. `rankTokenAddress` is the address of the rank token contract.
     *
     * Modifies:
     *
     * - Unlocks one rank token of `gameRank` for `player` in the rank token contract.
     */
    function _releaseRankToken(address player, uint256 gameRank, address rankTokenAddress) private {
        IRankToken rankToken = IRankToken(rankTokenAddress);
        rankToken.unlock(player, gameRank, 1);
    }

    /**
     * @dev Removes a player from a game and unlocks their rank tokens. `gameId` is the ID of the game. `player` is the address of the player to be removed.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     *
     * Modifies:
     *
     * - Removes `player` from the game.
     * - If the game rank is greater than 1, unlocks the game rank token for `player` in the rank token contract.
     */
    function removeAndUnlockPlayer(uint256 gameId, address player) public {
        enforceGameExists(gameId);
        gameId.removePlayer(player); //This will throw if game is in the process
        InstanceState storage instance = instanceState();
        GameState storage game = getGameState(gameId);
        if (game.rank > 1) {
            _releaseRankToken(player, game.rank, instance.commonParams.rankTokenAddress);
        }
    }

    /**
     * @dev Tries to make a move for a player in a game. `gameId` is the ID of the game. `player` is the address of the player.
     * The "move" is considered to be a state when player has made all actions he could in the given turn.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     *
     * Modifies:
     *
     * - If the player has not voted and a vote is expected, or if the player has not made a proposal and a proposal is expected, does not make a move and returns `false`.
     * - Otherwise, makes a move for `player` and returns `true`.
     */
    function tryPlayerMove(uint256 gameId, address player) public returns (bool) {
        uint256 turn = gameId.getTurn();
        GameState storage game = getGameState(gameId);
        bool expectVote = true;
        bool expectProposal = true;
        if (turn == 1) expectVote = false; // Don't expect votes at first turn
        // else if (gameId.isLastTurn()) expectProposal = false; // For now easiest solution is to keep collecting proposals as that is less complicated boundary case
        if (game.numPrevProposals < game.voting.minQuadraticPositions) expectVote = false; // If there is not enough proposals then round is skipped votes cannot be filled
        bool madeMove = true;
        if (expectVote && !game.playerVoted[player]) madeMove = false;
        if (expectProposal && game.proposalCommitment[player] == 0) madeMove = false;
        if (madeMove) gameId.playerMove(player);
        return madeMove;
    }

    /**
     * @dev Calculates the scores using a quadratic formula based on the revealed votes and proposer indices. `gameId` is the ID of the game. `votesRevealed` is an array of revealed votes. `proposerIndices` is an array of proposer indices that links proposals to index in getPlayers().
     *
     * Returns:
     *
     * - An array of updated scores for each player.
     * - An array of scores calculated for the current round.
     */
    function calculateScores(
        uint256 gameId,
        uint256[][] memory votesRevealed
    ) public returns (uint256[] memory, uint256[] memory) {
        address[] memory players = gameId.getPlayers();
        uint256[] memory scores = new uint256[](players.length);
        bool[] memory playerVoted = new bool[](players.length);
        GameState storage game = getGameState(gameId);
        // Convert mapping to array to pass it to libQuadratic
        for (uint256 i = 0; i < players.length; ++i) {
            playerVoted[i] = gameId._getState().isActive[players[i]];
        }
        uint256[] memory roundScores = game.voting.tallyVotes(votesRevealed, playerVoted);
        for (uint256 playerIdx = 0; playerIdx < players.length; playerIdx++) {
            //for each player
            if (game.proposalCommitment[players[playerIdx]] != 0) {
                //if player proposal exists
                scores[playerIdx] = gameId.getScore(players[playerIdx]) + roundScores[playerIdx];
                gameId.setScore(players[playerIdx], scores[playerIdx]);
            } else {
                //Player did not propose
                // TODO: implement tests for this
                // require(roundScores[playerIdx] == 0, "LibRankify->calculateScores: player got votes without proposing");
            }
        }
        return (scores, roundScores);
    }
}
","keccak256":"0x48edd2e70a0a14ebb747095839aef58f2a014fb3eb222a9674b3311024768829","license":"MIT"},"src/libraries/LibTurnBasedGame.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "hardhat/console.sol";
// import {EnumerableMap} from "@openzeppelin/contracts/utils/structs/EnumerableMap.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC1155} from "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {LibArray} from "../libraries/LibArray.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {IErrors} from "../interfaces/IErrors.sol";
/**
 * @title LibTBG
 * @dev Library for managing turn-based games.
 * It is designed to be used as a base library for games, and provides the following functionality:
 * - setting game settings such as time per turn, max players, min players, etc as well as perform score and leaderboard tracking
 *
 * Limitations:
 * - It is assumed there is only on game master per game
 *
 * ***WARNING*** Some limitations:
 * - This library is still under development and its interfaces may change.
 * - getting game data (which has own storage assignment and can be encapsulated from library) however there is no storage slot collision checks in place
 *
 */
library LibTBG {
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableSet for EnumerableSet.UintSet;

    struct Settings {
        uint256 timePerTurn;
        uint256 maxPlayerCnt;
        uint256 minPlayerCnt;
        uint256 timeToJoin;
        uint256 maxTurns;
        uint256 voteCredits;
        address gameMaster;
        bytes32 implementationStoragePointer;
    }

    struct State {
        uint256 currentTurn;
        uint256 turnStartedAt;
        uint256 registrationOpenAt;
        uint256 startedAt;
        bool hasStarted;
        bool hasEnded;
        EnumerableSet.AddressSet players;
        mapping(address => bool) madeMove;
        mapping(address => bool) isActive;
        uint256 numPlayersMadeMove;
        uint256 numActivePlayers;
        mapping(address => uint256) score;
        bool isOvertime;
        address[] leaderboard;
    }

    struct Instance {
        Settings settings;
        State state;
    }

    struct TBGStorageStruct {
        mapping(uint256 => Instance) instances;
        mapping(address => EnumerableSet.UintSet) playerInGames;
        uint256 totalGamesCreated;
    }

    bytes32 constant TBG_STORAGE_POSITION = keccak256("turn_based_game.storage.position");
    bytes32 constant IMPLEMENTATION_STORAGE_POSITION = keccak256("implementation.turn_based_game.storage.position");

    function TBGStorage() internal pure returns (TBGStorageStruct storage es) {
        bytes32 position = TBG_STORAGE_POSITION;
        assembly {
            es.slot := position
        }
    }

    function _getInstance(uint256 gameId) internal view returns (Instance storage) {
        TBGStorageStruct storage tbg = TBGStorage();
        return tbg.instances[gameId];
    }

    function _getState(uint256 gameId) internal view returns (State storage) {
        TBGStorageStruct storage tbg = TBGStorage();
        return tbg.instances[gameId].state;
    }

    /**
     * @dev Initializes the game with the provided settings. `settings` is the settings for the game.
     *
     * Requirements:
     *
     * - `settings.timePerTurn` must not be zero.
     * - `settings.maxPlayerCnt` must not be zero.
     * - `settings.minPlayerCnt` must be at least 2.
     * - `settings.maxTurns` must not be zero.
     * - `settings.timeToJoin` must not be zero.
     * - `settings.maxPlayerCnt` must not be less than `settings.minPlayerCnt`.
     * Modifies:
     *
     * - Sets the settings of the game to `settings`.
     */

    function init(uint256 gameId, Settings memory newSettings) private {
        TBGStorageStruct storage tbg = TBGStorage();
        Settings storage settings = tbg.instances[gameId].settings;
        require(newSettings.timePerTurn != 0, IErrors.invalidConfiguration("LibTBG::init->settings.timePerTurn"));
        require(newSettings.maxPlayerCnt != 0, IErrors.invalidConfiguration("LibTBG::init->settings.maxPlayerCnt"));
        require(newSettings.minPlayerCnt > 1, IErrors.invalidConfiguration("LibTBG::init->settings.minPlayerCnt"));
        require(newSettings.maxTurns != 0, IErrors.invalidConfiguration("LibTBG::init->settings.maxTurns"));
        require(newSettings.timeToJoin != 0, IErrors.invalidConfiguration("LibTBG::init->timeToJoin"));
        require(
            settings.minPlayerCnt < newSettings.maxPlayerCnt,
            IErrors.invalidConfiguration("LibTBG::init->maxPlayerCnt")
        );
        require(newSettings.gameMaster != address(0), IErrors.invalidConfiguration("LibTBG::init->gameMaster"));
        tbg.instances[gameId].settings = newSettings;
    }

    /**
     * @dev Creates a new game with the provided game ID and game master. `gameId` is the ID of the game. `gm` is the address of the game master.
     *
     * Requirements:
     *
     * - The game with `gameId` must not already exist.
     * - `gm` must not be the zero address.
     * - `gameId` must not be zero.
     * - The game master of the game with `gameId` must be the zero address.
     *
     * Modifies:
     *
     * - Sets the game master of the game with `gameId` to `gm`.
     * - Increments the total number of games created.
     */
    function createGame(uint256 gameId, Settings memory settings) internal {
        require(!gameExists(gameId), "createGame->Already exists");
        require(gameId != 0, "createGame->gameId");
        init(gameId, settings);
        TBGStorageStruct storage tbg = TBGStorage();
        tbg.totalGamesCreated += 1;

        //totalGamesCreated ensures nonce-like behavior:
        //even if game would get deleted and re-created with same name, data storage would be different
        tbg.instances[gameId].settings.implementationStoragePointer = keccak256(
            abi.encode(gameId, tbg.totalGamesCreated, TBG_STORAGE_POSITION)
        );
    }

    /**
     * @dev Deletes a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     *
     * Modifies:
     *
     * - Sets the game master, current turn, hasEnded, hasStarted,
     *   implementationStoragePointer, isOvertime, leaderboard, numPlayersMadeMove,
     *   players, registrationOpenAt, and turnStartedAt of the game with `gameId`
     *   to their initial values.
     * - Sets the score and madeMove of each player in the game with `gameId`
     *   to their initial values.
     */
    function deleteGame(uint256 gameId) internal {
        TBGStorageStruct storage tbg = TBGStorage();
        address[] memory players = tbg.instances[gameId].state.players.values();
        for (uint256 i = 0; i < players.length; ++i) {
            tbg.instances[gameId].state.score[players[i]] = 0;
            tbg.instances[gameId].state.madeMove[players[i]] = false;
            tbg.instances[gameId].state.isActive[players[i]] = false;
        }
        delete tbg.instances[gameId].state.currentTurn;
        delete tbg.instances[gameId].state.hasEnded;
        delete tbg.instances[gameId].state.hasStarted;
        delete tbg.instances[gameId].state.isOvertime;
        delete tbg.instances[gameId].state.leaderboard;
        delete tbg.instances[gameId].state.numPlayersMadeMove;
        delete tbg.instances[gameId].state.players;
        delete tbg.instances[gameId].state.registrationOpenAt;
        delete tbg.instances[gameId].state.turnStartedAt;
        delete tbg.instances[gameId].state.numActivePlayers;
        delete tbg.instances[gameId].settings.gameMaster;
        delete tbg.instances[gameId].settings.implementationStoragePointer;
        delete tbg.instances[gameId].settings.timePerTurn;
        delete tbg.instances[gameId].settings.maxPlayerCnt;
        delete tbg.instances[gameId].settings.minPlayerCnt;
        delete tbg.instances[gameId].settings.timeToJoin;
        delete tbg.instances[gameId].settings.maxTurns;
        delete tbg.instances[gameId].settings.voteCredits;
    }

    /**
     * @dev Checks if a game with the provided game ID can be joined. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - A boolean indicating whether the game can be joined.
     */
    function canBeJoined(uint256 gameId) internal view returns (bool) {
        State storage state = _getState(gameId);
        if (state.hasStarted || state.registrationOpenAt == 0) return false;
        return true;
    }

    /**
     * @dev Adds a player to a game with the provided game ID. `gameId` is the ID of the game. `participant` is the address of the player.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     * - `participant` must not already be in a game.
     * - The number of players in the game with `gameId` must be less than the maximum number of players.
     * - The game with `gameId` must be joinable.
     *
     * Modifies:
     *
     * - Adds `participant` to the players of the game with `gameId`.
     * - Sets the madeMove of `participant` in the game with `gameId` to false.
     * - Sets the game of `participant` to `gameId`.
     */
    function addPlayer(uint256 gameId, address participant) internal {
        TBGStorageStruct storage tbg = TBGStorage();
        State storage state = tbg.instances[gameId].state;
        Settings storage settings = tbg.instances[gameId].settings;
        require(gameExists(gameId), "addPlayer->invalid game");

        require(!tbg.playerInGames[participant].contains(gameId), "addPlayer->Player in game");
        require(state.players.length() < settings.maxPlayerCnt, "addPlayer->party full");

        require(canBeJoined(gameId), "addPlayer->cant join now");
        state.players.add(participant);
        state.madeMove[participant] = false;
        state.isActive[participant] = false;
        tbg.playerInGames[participant].add(gameId);
    }

    /**
     * @dev Checks if a player is in a game with the provided game ID. `gameId` is the ID of the game. `player` is the address of the player.
     *
     * Returns:
     *
     * - A boolean indicating whether the player is in the game.
     */
    function isPlayerInGame(uint256 gameId, address player) internal view returns (bool) {
        TBGStorageStruct storage tbg = TBGStorage();
        return tbg.playerInGames[player].contains(gameId);
    }

    /**
     * @dev Removes a player from a game with the provided game ID. `gameId` is the ID of the game. `participant` is the address of the player.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     * - `participant` must be in the game with `gameId`.
     * - The game with `gameId` must not have started or must have ended.
     *
     * Modifies:
     *
     * - Sets the game of `participant` to 0.
     * - Removes `participant` from the players of the game with `gameId`.
     */
    function removePlayer(uint256 gameId, address participant) internal {
        TBGStorageStruct storage tbg = TBGStorage();
        State storage state = tbg.instances[gameId].state;
        require(gameExists(gameId), "game does not exist");
        require(tbg.playerInGames[participant].contains(gameId), "Not in the game");
        require(state.hasStarted == false || state.hasEnded == true, "Cannot leave once started");
        tbg.playerInGames[participant].remove(gameId);
        state.players.remove(participant);
    }

    /**
     * @dev Checks if the current turn in a game with the provided game ID has timed out. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - `gameId` must not be zero.
     * - The game with `gameId` must have started.
     *
     * Returns:
     *
     * - A boolean indicating whether the current turn has timed out.
     */
    function isTurnTimedOut(uint256 gameId) internal view returns (bool) {
        TBGStorageStruct storage tbg = TBGStorage();
        State storage state = _getState(gameId);
        assert(gameId != 0);
        assert(state.hasStarted == true);
        if (block.timestamp <= tbg.instances[gameId].settings.timePerTurn + state.turnStartedAt) return false;
        return true;
    }

    /**
     * @dev Checks if a game with the provided game ID exists. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - A boolean indicating whether the game exists.
     */
    function gameExists(uint256 gameId) internal view returns (bool) {
        Settings storage settings = getSettings(gameId);
        if (settings.gameMaster != address(0)) return true;
        return false;
    }

    /**
     * @dev Enforces that a game with the provided game ID has started. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - `gameId` must not be zero.
     * - The game with `gameId` must have started.
     */
    function enforceHasStarted(uint256 gameId) internal view {
        State storage state = _getState(gameId);
        assert(gameId != 0);
        require(state.hasStarted, "Game has not yet started");
    }

    /**
     * @dev Enforces that a game with the provided game ID has started. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - `gameId` must not be zero.
     * - The game with `gameId` must have started.
     *
     */
    function canEndTurn(uint256 gameId) internal view returns (bool) {
        bool turnTimedOut = isTurnTimedOut(gameId);
        State storage state = _getState(gameId);
        if (!state.hasStarted || isGameOver(gameId)) return false;
        if (turnTimedOut) return true;
        return false;
    }

    /**
     * @dev Checks if the current turn in a game with the provided game ID can end early. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - A boolean indicating whether the current turn can end early.
     */
    function canEndTurnEarly(uint256 gameId) internal view returns (bool) {
        State storage state = _getState(gameId);
        if (!state.hasStarted || isGameOver(gameId)) return false;

        uint256 activePlayersNotMoved = 0;
        address[] memory players = state.players.values();
        for (uint256 i = 0; i < players.length; i++) {
            if (state.isActive[players[i]] && !state.madeMove[players[i]]) {
                activePlayersNotMoved++;
            }
        }
        return activePlayersNotMoved == 0 || canEndTurn(gameId);
    }

    /**
     * @dev Modifier that requires the current turn in a game with the provided game ID to be able to end. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - The current turn in the game with `gameId` must be able to end.
     */
    modifier onlyInTurnTime(uint256 gameId) {
        require(isTurnTimedOut(gameId) == false, "onlyInTurnTime -> turn timeout");
        _;
    }

    modifier onlyWhenTurnCanEnd(uint256 gameId) {
        require(canEndTurn(gameId) == true, "onlyWhenTurnCanEnd: Not everyone made a move yet and there still is time");
        _;
    }

    /**
     * @dev Resets the states of the players in a game. `State` is the state.
     *
     * Modifies:
     *
     * - Sets the madeMove and score of each player in `game` to their initial values.
     */
    function _resetPlayerStates(State storage state) internal {
        for (uint256 i = 0; i < state.players.length(); ++i) {
            address player = state.players.at(i);
            state.madeMove[player] = false;
            state.score[player] = 0;
        }
    }

    /**
     * @dev Sets the score of a player in a game with the provided game ID. `gameId` is the ID of the game. `player` is the address of the player. `value` is the score.
     *
     * Requirements:
     *
     * - `player` must be in the game with `gameId`.
     *
     * Modifies:
     *
     * - Sets the score of `player` in the game with `gameId` to `value`.
     */
    function setScore(uint256 gameId, address player, uint256 value) internal {
        State storage state = _getState(gameId);
        require(isPlayerInGame(gameId, player), "player not in a game");
        state.score[player] = value;
    }

    /**
     * @dev Gets the score of a player in a game with the provided game ID. `gameId` is the ID of the game. `player` is the address of the player.
     *
     * Returns:
     *
     * - The score of `player` in the game with `gameId`.
     */
    function getScore(uint256 gameId, address player) internal view returns (uint256) {
        State storage state = _getState(gameId);
        return state.score[player];
    }

    /**
     * @dev Gets the scores of the players in a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - An array of the addresses of the players in the game with `gameId`.
     * - An array of the scores of the players in the game with `gameId`.
     */
    function getScores(uint256 gameId) internal view returns (address[] memory, uint256[] memory) {
        address[] memory players = getPlayers(gameId);
        uint256[] memory scores = new uint256[](players.length);
        for (uint256 i = 0; i < players.length; ++i) {
            scores[i] = getScore(gameId, players[i]);
        }
        return (players, scores);
    }

    /**
     * @dev Opens registration for a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     *
     * Modifies:
     *
     * - Sets the registrationOpenAt of the game with `gameId` to the current block timestamp.
     */
    function openRegistration(uint256 gameId) internal {
        require(gameExists(gameId), "game not found");
        State storage state = _getState(gameId);
        state.registrationOpenAt = block.timestamp;
    }

    /**
     * @dev Checks if registration is open for a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - A boolean indicating whether registration is open for the game.
     */
    function isRegistrationOpen(uint256 gameId) internal view returns (bool) {
        State storage state = _getState(gameId);
        TBGStorageStruct storage tbg = TBGStorage();
        if (state.registrationOpenAt == 0) {
            return false;
        } else {
            return
                state.registrationOpenAt < block.timestamp + tbg.instances[gameId].settings.timeToJoin ? true : false;
        }
    }

    /**
     * @dev Checks if a game with the provided game ID can start. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - A boolean indicating whether the game can start.
     */
    function canStart(uint256 gameId) internal view returns (bool) {
        State storage state = _getState(gameId);
        TBGStorageStruct storage tbg = TBGStorage();
        if (state.hasStarted) return false;
        if (state.registrationOpenAt == 0) return false;
        if (gameId == 0) return false;
        if (block.timestamp <= state.registrationOpenAt + tbg.instances[gameId].settings.timeToJoin) return false;
        if (state.players.length() < tbg.instances[gameId].settings.minPlayerCnt) return false;
        return true;
    }

    /**
     * @dev Checks if a game with the provided game ID can start early. `gameId` is the ID of the game.
     * By "early" it is assumed that time to join has not yet passed, but it's already cap players limit reached.
     *
     * Returns:
     *
     * - A boolean indicating whether the game can start early.
     */
    function canStartEarly(uint256 gameId) internal view returns (bool) {
        State storage state = _getState(gameId);
        TBGStorageStruct storage tbg = TBGStorage();

        if ((state.players.length() == tbg.instances[gameId].settings.maxPlayerCnt) || canStart(gameId)) return true;
        return false;
    }

    /**
     * @dev Internal function to perform common game start operations
     * @param gameId The ID of the game to start
     * @param state The game state storage reference
     * @param tbg The TBG storage reference
     */
    function _performGameStart(uint256 gameId, State storage state, TBGStorageStruct storage tbg) private {
        require(state.hasStarted == false, "startGame->already started");
        require(state.registrationOpenAt != 0, "startGame->Game registration was not yet open");
        require(gameId != 0, "startGame->Game not found");
        require(state.players.length() >= tbg.instances[gameId].settings.minPlayerCnt, "startGame->Not enough players");

        state.hasStarted = true;
        state.hasEnded = false;
        state.currentTurn = 1;
        state.turnStartedAt = block.timestamp;
        state.startedAt = block.timestamp;
        _resetPlayerStates(state);

        // Initialize all players as active
        uint256 playerCount = state.players.length();
        state.numActivePlayers = playerCount;
        for (uint256 i = 0; i < playerCount; i++) {
            address player = state.players.at(i);
            state.isActive[player] = true;
        }
    }

    /**
     * @dev Starts a game with the provided game ID early. `gameId` is the ID of the game.
     * By "early" it is assumed that time to join has not yet passed, but it's already cap players limit reached.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     * - The game with `gameId` must not have started.
     * - The game with `gameId` must have opened registration.
     * - The number of players in the game with `gameId` must be greater than or equal to the minimum number of players.
     * - The number of players in the game with `gameId` must be equal to the maximum number of players or the current block timestamp must be greater than the registration open time plus the time to join.
     *
     * Modifies:
     *
     * - Sets the hasStarted, hasEnded, currentTurn, and turnStartedAt of the game with `gameId` to their new values.
     * - Resets the states of the players in the game with `gameId`.
     */
    function startGameEarly(uint256 gameId) internal {
        State storage state = _getState(gameId);
        TBGStorageStruct storage tbg = TBGStorage();

        require(
            (state.players.length() == tbg.instances[gameId].settings.maxPlayerCnt) ||
                (block.timestamp > state.registrationOpenAt + tbg.instances[gameId].settings.timeToJoin),
            "startGame->Not enough players"
        );

        _performGameStart(gameId, state, tbg);
    }

    /**
     * @dev Starts a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - The game with `gameId` must exist.
     * - The game with `gameId` must not have started.
     * - The game with `gameId` must have opened registration.
     * - The current block timestamp must be greater than the registration open time plus the time to join.
     *
     * Modifies:
     *
     * - Sets the hasStarted, hasEnded, currentTurn, and turnStartedAt of the game with `gameId` to their new values.
     * - Resets the states of the players in the game with `gameId`.
     */
    function startGame(uint256 gameId) internal {
        State storage state = _getState(gameId);
        TBGStorageStruct storage tbg = TBGStorage();

        require(
            block.timestamp > state.registrationOpenAt + tbg.instances[gameId].settings.timeToJoin,
            "startGame->Still Can Join"
        );

        _performGameStart(gameId, state, tbg);
    }

    /**
     * @dev Gets the current turn of a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - The current turn of the game with `gameId`.
     */
    function getTurn(uint256 gameId) internal view returns (uint256) {
        State storage state = _getState(gameId);
        return state.currentTurn;
    }

    /**
     * @dev Gets the game master of a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - The game master of the game with `gameId`.
     */
    function getGM(uint256 gameId) internal view returns (address) {
        Settings storage settings = getSettings(gameId);
        return settings.gameMaster;
    }

    /**
     * @dev Checks if the current turn is the last turn in a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - A boolean indicating whether the current turn is the last turn in the game.
     */
    function isLastTurn(uint256 gameId) internal view returns (bool) {
        TBGStorageStruct storage tbg = TBGStorage();
        State storage state = _getState(gameId);
        if (state.currentTurn == tbg.instances[gameId].settings.maxTurns) return true;
        else return false;
    }

    /**
     * @dev Checks if a game with the provided game ID is over. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - A boolean indicating whether the game is over.
     */
    function isGameOver(uint256 gameId) internal view returns (bool) {
        TBGStorageStruct storage tbg = TBGStorage();
        State storage state = _getState(gameId);
        if ((state.currentTurn > tbg.instances[gameId].settings.maxTurns) && !state.isOvertime) return true;
        else return false;
    }

    /**
     * @dev Enforces that a game with the provided game ID is not over. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - The game with `gameId` must not be over.
     */
    function enforceIsNotOver(uint256 gameId) internal view {
        require(!isGameOver(gameId), "Game over");
    }

    /**
     * @dev Records a player's move in a game with the provided game ID. `gameId` is the ID of the game. `player` is the address of the player.
     *
     * Requirements:
     *
     * - The game with `gameId` must have started.
     * - The game with `gameId` must not be over.
     * - `player` must not have made a move in the current turn of the game with `gameId`.
     * - `player` must be in the game with `gameId`.
     *
     * Modifies:
     *
     * - Sets the madeMove of `player` in the game with `gameId` to true.
     * - Increments the numPlayersMadeMove of the game with `gameId`.
     */
    function playerMove(uint256 gameId, address player) internal onlyInTurnTime(gameId) {
        State storage state = _getState(gameId);
        enforceHasStarted(gameId);
        enforceIsNotOver(gameId);
        require(state.madeMove[player] == false, "already made a move");
        TBGStorageStruct storage tbg = TBGStorage();
        require(tbg.playerInGames[player].contains(gameId), "is not in the game");
        state.madeMove[player] = true;
        state.numPlayersMadeMove += 1;

        // Set player as active when they make a move
        state.isActive[player] = true;
        state.numActivePlayers++;
    }

    function isPlayerTurnComplete(uint256 gameId, address player) internal view returns (bool) {
        State storage state = _getState(gameId);
        return state.madeMove[player];
    }

    /**
     * @dev Enforces that a player is in a game with the provided game ID. `gameId` is the ID of the game. `player` is the address of the player.
     *
     * Requirements:
     *
     * - `player` must be in the game with `gameId`.
     */
    function enforceIsPlayingGame(uint256 gameId, address player) internal view {
        TBGStorageStruct storage tbg = TBGStorage();
        require(tbg.playerInGames[player].contains(gameId), "is not in the game");
    }

    /**
     * @dev Checks if a game with the provided game ID has started. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - A boolean indicating whether the game has started.
     */
    function hasStarted(uint256 gameId) internal view returns (bool) {
        State storage state = _getState(gameId);
        return state.hasStarted;
    }

    /**
     * @dev Gets the leaderboard of a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - An array of the addresses of the players in the game with `gameId`, sorted by score.
     */
    function getLeaderBoard(uint256 gameId) internal view returns (address[] memory) {
        State storage state = _getState(gameId);
        return state.leaderboard;
    }

    /**
     * @dev Advances to the next turn in a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - The game with `gameId` must be able to end the current turn early. (all players have moved or the turn has timed out)
     *
     * Modifies:
     *
     * - Clears the current moves in the game with `gameId`.
     * - Increments the currentTurn of the game with `gameId`.
     * - Sets the turnStartedAt of the game with `gameId` to the current block timestamp.
     * - If the current turn is the last turn or the game with `gameId` is in overtime, checks if the game is a tie and sets the isOvertime of the game with `gameId` to the result.
     * - Sets the hasEnded of the game with `gameId` to whether the game is over.
     *
     * Returns:
     *
     * - A boolean indicating whether the current turn is the last turn.
     * - A boolean indicating whether the game is a tie.
     * - A boolean indicating whether the game is over.
     */
    function nextTurn(uint256 gameId) internal returns (bool, bool, bool) {
        require(canEndTurnEarly(gameId), "nextTurn->CanEndEarly");
        State storage state = _getState(gameId);
        bool wasLastTurn = isLastTurn(gameId);
        state.currentTurn += 1;
        state.turnStartedAt = block.timestamp;
        bool _isLastTurn = isLastTurn(gameId);
        if (wasLastTurn || state.isOvertime) {
            bool _isTie = isTie(gameId);
            state.isOvertime = _isTie;
        }
        state.hasEnded = isGameOver(gameId);

        // Update player activity status for next turn
        uint256 playerCount = state.players.length();
        state.numActivePlayers = 0;

        for (uint256 i = 0; i < playerCount; i++) {
            address player = state.players.at(i);
            // If player didn't make a move this turn, mark them as inactive
            if (!state.madeMove[player]) {
                // console.log('LibTBG::nextTurn - ','player inactive!');
                state.isActive[player] = false;
            } else {
                // console.log('LibTBG::nextTurn - ','player active!');
                state.numActivePlayers++;
            }
            state.madeMove[player] = false;
        }
        state.numPlayersMadeMove = 0;

        (state.leaderboard, ) = sortByScore(gameId);
        return (_isLastTurn, state.isOvertime, state.hasEnded);
    }

    /**
     * @dev Gets the data storage pointer.
     *
     * Returns:
     *
     * - The data storage pointer.
     */
    function getDataStorage() internal pure returns (bytes32 pointer) {
        return IMPLEMENTATION_STORAGE_POSITION;
    }

    /**
     * @dev Gets the game data storage pointer of a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - The game data storage pointer of the game with `gameId`.
     */
    function getGameDataStorage(uint256 gameId) internal view returns (bytes32 pointer) {
        Settings storage settings = getSettings(gameId);
        return settings.implementationStoragePointer;
    }

    /**
     * @dev Gets the number of players in a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - The number of players in the game with `gameId`.
     */
    function getPlayersNumber(uint256 gameId) internal view returns (uint256) {
        State storage state = _getState(gameId);
        return state.players.length();
    }

    /**
     * @dev Gets the players in a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - An array of the addresses of the players in the game with `gameId`.
     */
    function getPlayers(uint256 gameId) internal view returns (address[] memory) {
        State storage state = _getState(gameId);
        return state.players.values();
    }

    /**
     * @dev Gets the game settings.
     *
     * Returns:
     *
     * - The game settings.
     */
    function getSettings(uint256 gameId) internal view returns (Settings storage) {
        TBGStorageStruct storage tbg = TBGStorage();
        return tbg.instances[gameId].settings;
    }

    /**
     * @dev Enforces that a game with the provided game ID is in the pre-registration stage. `gameId` is the ID of the game.
     *
     * Requirements:
     *
     * - Registration must not be open for the game with `gameId`.
     * - The game with `gameId` must not have started.
     */
    function enforceIsPreRegistrationStage(uint256 gameId) internal view {
        require(!isRegistrationOpen(gameId), "Cannot do when registration is open");
        require(!hasStarted(gameId), "Cannot do when game started");
    }

    /**
     * @dev Adds overtime to a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Modifies:
     *
     * - Sets the isOvertime of the game with `gameId` to true.
     */
    function addOvertime(uint256 gameId) internal {
        State storage state = _getState(gameId);
        state.isOvertime = true;
    }

    /**
     * @dev Checks if a game with the provided game ID is in overtime. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - A boolean indicating whether the game is in overtime.
     */
    function isOvertime(uint256 gameId) internal view returns (bool) {
        State storage state = _getState(gameId);
        return state.isOvertime;
    }

    /**
     * @dev Resets the overtime of a game with the provided game ID. `gameId` is the ID of the game.
     *
     * Modifies:
     *
     * - Sets the isOvertime of the game with `gameId` to false.
     */
    function resetOvertime(uint256 gameId) internal {
        State storage state = _getState(gameId);
        state.isOvertime = false;
    }

    /**
     * @dev Checks if a game with the provided game ID is a tie. `gameId` is the ID of the game.
     * Tie being defined as at least two of the top `numWinners=1` players having the same score.
     *
     * Returns:
     *
     * - A boolean indicating whether the game is a tie.
     */
    function isTie(uint256 gameId) internal view returns (bool) {
        (, uint256[] memory scores) = getScores(gameId);

        LibArray.quickSort(scores, int256(0), int256(scores.length - 1));

        if (scores[0] == scores[1]) {
            return (true);
        }

        return (false);
    }

    /**
     * @dev Gets the game ID of the game a player is in. `player` is the address of the player.
     *
     * Returns:
     *
     * - The game ID of the game `player` is in.
     */
    function getPlayersGames(address player) internal view returns (uint256[] memory) {
        TBGStorageStruct storage tbg = TBGStorage();

        return tbg.playerInGames[player].values();
    }

    /**
     * @dev Sorts the players and scores arrays in descending order of scores using the quicksort algorithm. `players` is the array of player addresses. `scores` is the array of scores. `left` is the left index. `right` is the right index.
     *
     * Modifies:
     *
     * - Sorts the `players` and `scores` arrays in place.
     */
    function _quickSort(address[] memory players, uint256[] memory scores, int256 left, int256 right) private view {
        int256 i = left;
        int256 j = right;
        if (i == j) return;
        uint256 pivot = scores[uint256(left + (right - left) / 2)];
        while (i <= j) {
            while (scores[uint256(i)] > pivot) ++i;
            while (pivot > scores[uint256(j)]) j--;
            if (i <= j) {
                (scores[uint256(i)], scores[uint256(j)]) = (scores[uint256(j)], scores[uint256(i)]);
                (players[uint256(i)], players[uint256(j)]) = (players[uint256(j)], players[uint256(i)]);
                ++i;
                j--;
            }
        }
        if (left < j) _quickSort(players, scores, left, j);
        if (i < right) _quickSort(players, scores, i, right);
    }

    /**
     * @dev Sorts the players in a game with the provided game ID by score in descending order. `gameId` is the ID of the game.
     *
     * Returns:
     *
     * - An array of the addresses of the players in the game with `gameId`, sorted by score.
     * - An array of the scores of the players in the game with `gameId`, sorted in descending order.
     */
    function sortByScore(uint256 gameId) internal view returns (address[] memory, uint256[] memory) {
        (address[] memory players, uint256[] memory scores) = getScores(gameId);
        _quickSort(players, scores, 0, int256(scores.length - 1));
        return (players, scores);
    }

    function isActive(uint256 gameId, address player) internal view returns (bool) {
        State storage state = _getState(gameId);
        return state.isActive[player];
    }
}
