import {LibMultipass} from "../libraries/LibMultipass.sol";

/**
 * @title IMultipass
 * @notice Interface for the Multipass contract. Multipass contract acts as cross-domain registry, allowing owner to specify registrars and domains that can be used to register names.
 * It also allows for referral program, where referrers can earn rewards for referring new registrations.
 * @custom:security-contact sirt@peeramid.xyz
 */
interface IMultipass {
    enum InvalidQueryReasons {
        EMPTY_ID,
        EMPTY_DOMAIN,
        EMPTY_ADDRESS
    }

    error invalidQuery(InvalidQueryReasons reason);
    error nameExists(bytes32 name);
    error recordExists(LibMultipass.Record newRecord);
    error isActive(bytes32 name, bool isActive);
    error signatureExpired(uint256 signatureDeadline);
    error invalidSignature();
    error mathOverflow(uint256 a, uint256 b);
    error invalidDomain(bytes32 domainName);
    error referralRewardsTooHigh(uint256 referrerReward, uint256 referralDiscount, uint256 fee);
    error invalidRegistrar(address registrar);
    error paymentTooLow(uint256 fee, uint256 value);
    error paymendFailed();
    error referredSelf();
    error domainNotActive(bytes32 domainName);
    error userNotFound(LibMultipass.NameQuery query);
    error invalidnameChange(bytes32 domainName, bytes32 newName);
    error invalidNonce(uint256 nonce);
    error invalidNonceIncrement(uint256 old, uint256 newer);

    /**
     * @dev Retrieves the resolved record for a given name query.
     * @param query The name query to resolve.
     * @return A boolean indicating whether the record was found, and the resolved record.
     */
    function resolveRecord(
        LibMultipass.NameQuery memory query
    ) external view returns (bool, LibMultipass.Record memory);

    /**
     * @dev Initializes new LibMultipass.Domain and configures it's parameters
     *
     * Requirements:
     *  registrar is not zero
     *  domainName is not empty
     *  domainIndex is either zero(auto assign) or can be one of preoccupied LibMultipass.Domain names
     *  domainName does not exist yet
     *  onlyOwner
     *  referrerReward+referralDiscount cannot be larger than fee
     *  @param registrar address of registrar
     *  @param fee fee in base currency of network
     *  @param domainName name of LibMultipass.Domain
     *  @param referrerReward referral fee share in base currency of network
     *  @param referralDiscount referral discount in base currency of network
     *
     *  Emits an {InitializedDomain} event.
     */
    function initializeDomain(
        address registrar,
        uint256 fee,
        uint256 renewalFee,
        bytes32 domainName,
        uint256 referrerReward,
        uint256 referralDiscount
    ) external;

    /**
     * @dev Activates LibMultipass.Domain name
     *
     * Requirements:
     *  msg.sender is Owner
     *
     *
     *  Emits an {DomainActivated} event.
     */
    function activateDomain(bytes32 domainName) external;

    /**
     * @dev Deactivates LibMultipass.Domain name
     *
     * Deactivated LibMultipass.Domain cannot mutate names and will return zeros
     *
     * Requirements:
     *  msg.sender is Owner OR registrar
     *
     *
     *  Emits an {DomainDeactivated} event.
     */
    function deactivateDomain(bytes32 domainName) external;

    /**
     * @dev Changes registrar address
     *
     * Requirements:
     *  msg.sender is Owner
     *
     *  Emits an {DomainFeeChanged} event.
     */
    function changeFee(bytes32 domainName, uint256 fee) external;

    /**
     * @dev Changes registrar address
     *
     * Requirements:
     *  msg.sender is Owner
     *
     *  Emits an {RegistrarChangeRequested} event.
     */
    function changeRegistrar(bytes32 domainName, address newRegistrar) external;

    /**
     * @dev deletes name
     *
     * Requirements:
     *  msg.sender is Owner
     *
     *  Emits an {DomainTTLChangeRequested} event.
     */
    function deleteName(LibMultipass.NameQuery memory query) external;

    /**
     * @dev executes all pending changes to LibMultipass.Domain that fulfill TTL
     *
     * Requirements:
     *  domainName must be set
     *  referrerFeeShare+referralDiscount cannot be larger than 2^32
     *
     *
     *  Emits an {ReferralProgramChangeRequested} event.
     */
    function changeReferralProgram(uint256 referrerFeeShare, uint256 referralDiscount, bytes32 domainName) external;

    /**
     * @dev changes renewal fee for domain
     *
     * Requirements:
     *  domainName must be set
     *  fee must be set
     *
     *
     *  Emits an {RenewalFeeChangeRequested} event.
     */
    function changeRenewalFee(uint256 fee, bytes32 domainName) external;

    /**
     * @dev registers new name under LibMultipass.Domain
     *
     * Requirements:
     *  all arguments must be set
     *  domainName must be active
     * resolveRecord for given arguments should return no LibMultipass.Record
     *
     *
     *  Emits an {registered} event.
     */
    function register(
        LibMultipass.Record memory newRecord,
        bytes memory registrarSignature,
        LibMultipass.NameQuery memory referrer,
        bytes memory referralCode
    ) external payable;

    /**
     * @dev returns LibMultipass.Domain state variables
     * @param domainName name of the LibMultipass.Domain
     * @return (name,
      fee,
       referrerReward,
       referralDiscount,
       isActive,
       registrar,
       ttl,
        registerSize)
     */
    function getDomainState(bytes32 domainName) external view returns (LibMultipass.Domain memory);

    /**
     * @dev returns contract state variables

     * @return (s_numDomains)
     */
    function getContractState() external view returns (uint256);
    /**
     * @dev returns price for registering name
     *
     */
    event fundsWithdawn(uint256 indexed amount, address indexed account);

    /**
     * @dev Initializes a new domain with the specified parameters.
     * @param registrar The address of the registrar for the domain.
     * @param fee The fee required for registration in the domain.
     * @param domainName The name of the domain.
     * @param referrerReward The reward for referring new registrations to the domain.
     * @param referralDiscount The discount for referrals in the domain.
     */
    event InitializedDomain(
        address indexed registrar,
        uint256 indexed fee,
        bytes32 indexed domainName,
        uint256 renewalFee,
        uint256 referrerReward,
        uint256 referralDiscount
    );

    /**
     * @dev Emitted when a domain is activated.
     * @param domainName The name of the activated domain.
     */
    event DomainActivated(bytes32 indexed domainName);

    /**
     * @dev Emitted when a domain is deactivated.
     * @param domainName The name of the deactivated domain.
     */
    event DomainDeactivated(bytes32 indexed domainName);

    /**
     * @dev Emitted when the fee for a domain is changed.
     * @param domainName The name of the domain.
     * @param newFee The new fee for the domain.
     */
    event DomainFeeChanged(bytes32 indexed domainName, uint256 indexed newFee);

    /**
     * @dev Emitted when a registrar change is requested for a domain.
     * @param domainName The name of the domain.
     * @param registrar The address of the new registrar.
     */
    event RegistrarChanged(bytes32 indexed domainName, address indexed registrar);

    /**
     * @dev Emitted when a name is deleted.
     * @param domainName The domain name.
     * @param wallet The address of the wallet.
     * @param id The ID of the name.
     * @param name The name.
     */
    event nameDeleted(bytes32 indexed domainName, address indexed wallet, bytes32 indexed id, bytes32 name);

    /**
     * @dev Emitted when the referral program for a domain is changed.
     * @param domainName The domain name.
     * @param reward The referral reward amount.
     * @param discount The referral discount amount.
     */
    event ReferralProgramChanged(bytes32 indexed domainName, uint256 reward, uint256 discount);

    /**
     * @dev Emitted when a domain is registered.
     * @param domainName The domain name.
     * @param NewRecord The new record.
     */
    event Registered(bytes32 indexed domainName, LibMultipass.Record NewRecord);

    /**
     * @dev Emitted when a user is referred.
     * @param refferrer The record of the referrer.
     * @param newRecord The new record.
     * @param domainName The domain name.
     */
    event Referred(LibMultipass.Record refferrer, LibMultipass.Record newRecord, bytes32 indexed domainName);

    /**
     * @dev Emitted when a user record is renewed.
     * @param wallet The address of the wallet.
     * @param domainName The domain name.
     * @param id The ID of the record.
     * @param newRecord The new record.
     */
    event Renewed(
        address indexed wallet,
        bytes32 indexed domainName,
        bytes32 indexed id,
        LibMultipass.Record newRecord
    );

    /**
     * @dev Emitted when a domain renewal fee is changed.
     * @param domainName The domain name.
     * @param newFee The new renewal fee.
     */
    event RenewalFeeChanged(bytes32 indexed domainName, uint256 indexed newFee);

    /**
     * @dev Retrieves the domain state by its ID.
     * @param id The ID of the domain.
     * @return The domain state as a `LibMultipass.Domain` struct.
     */
    function getDomainStateById(uint256 id) external view returns (LibMultipass.Domain memory);

    /**
     * @notice renews record for given query
     * @param query name query
     * @param record new record
     * @param registrarSignature registrar signature
     */
    function renewRecord(
        LibMultipass.NameQuery memory query,
        LibMultipass.Record memory record,
        bytes memory registrarSignature
    ) external payable;
}
","keccak256":"0x790366d3e7a3904d0d2cee4a96b0044ee2288aa359aaf80c72d0b45dbb7e617f","license":"MIT"},"@peeramid-labs/multipass/src/libraries/LibMultipass.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity =0.8.28;
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title LibMultipass
 * @notice Library for handling multipass functionality.
 *
 * This library provides a set of functions to manage and utilize multipass features.
 * It is designed to be used as a part of the multipass system within the project.
 * @custom:security-contact sirt@peeramid.xyz
 */
library LibMultipass {
    /**
     * @dev resolves user from any given argument
     * Requirements:
     *  domainName must be given and must be initialized
     *  id OR username OR address must be given
     * This method first tries to resolve by address, then by user id and finally by username
     * @param domainName domain name
     * @param wallet adress of user
     * @param id user id
     * @param username username
     * @param targetDomain if this is set to valid domain name, then after sucessfull resolving account at domainName,
     *                       this method will rerun with resolving user properties in targetDomain
     */
    struct NameQuery {
        bytes32 domainName;
        address wallet;
        bytes32 name;
        bytes32 id;
        bytes32 targetDomain;
    }

    /**
     * @dev The domain name of the registrar.
     * @param registrar is the address private key of which is owned by signing server (e.g. Discord bot server)
     * @param name is unique string that is used to find this domain within domains.

     * @param fee amount of payment requried to register name in the domain
     * @param ttl time to live for changes in the domain properties
     * @param isActive when is false domain name will not respond to any changes and will not return any address
    **/
    struct Domain {
        bytes32 name; //32bytes
        uint256 fee; //32bytes
        uint256 referrerReward; //32bytes
        uint256 referralDiscount; //32bytes
        bool isActive; //1byte
        address registrar; //20 bytes
        uint24 ttl; //3 bytes (not being used for now)
        uint256 registerSize; //32bytes
        uint256 renewalFee; //32bytes
    }

    /**
     * @dev The record in the registry.
     * @param wallet is the address of the user
     * @param name is the name of the user
     * @param id is the unique identificator of the user
     * @param nonce is the number of changes in the user record
     * @param domainName is the domain name of the registrar
     **/
    struct Record {
        address wallet;
        bytes32 name;
        bytes32 id;
        uint96 nonce;
        bytes32 domainName;
        uint256 validUntil;
    }

    bytes32 private constant MULTIPASS_STORAGE_POSITION = bytes32(uint256(keccak256("multipass.storage.struct")) - 1);

    /**
     * @dev The domain name of the registrar.
     * @param properties - domain configuration
     * @param idToAddress is mapping from unique identificator to an address
     * @param registerSize is number of registered users for this domain
     * @param nonce is incremented each time Record changes in addressToId map
     * @param nameToId is mapping from names to unique identificator. While each name required to be unique,
                        names might change on the domain, so we keep records to user identificators as immutable property of user
     * @param addressToId is mapping from an address to unique identificator
     * @param idToName is mapping from identificator to a name
    **/
    struct DomainStorage {
        Domain properties; //128 bytes
        mapping(bytes32 => address) idToAddress; //N*20bytes
        mapping(bytes32 => uint96) nonce; //N*12bytes
        mapping(address => bytes32) addressToId; //N*32 bytes
        mapping(bytes32 => bytes32) nameToId; //N*32 bytes
        mapping(bytes32 => bytes32) idToName; //N*32 bytes
        mapping(address => uint256) validUntil; //N*32 bytes
    }

    /**
     * @dev The storage structure for the Multipass contract.
     * @param domains is mapping from domain index to domain properties
     * @param domainNameToIndex is mapping from domain name to domain index
     */
    struct MultipassStorageStruct {
        mapping(uint256 => DomainStorage) domains;
        mapping(bytes32 => uint256) domainNameToIndex; //helper to get domain index by name
        uint256 numDomains;
    }

    /**
     * @dev Returns the storage struct for the Multipass contract.
     */
    function MultipassStorage() private pure returns (MultipassStorageStruct storage es) {
        bytes32 position = MULTIPASS_STORAGE_POSITION;
        assembly {
            es.slot := position
        }
    }

    bytes32 internal constant _TYPEHASH =
        keccak256("registerName(bytes32 name,bytes32 id,bytes32 domainName,uint256 validUntil,uint96 nonce)");
    bytes32 internal constant _TYPEHASH_REFERRAL = keccak256("proofOfReferrer(address referrerAddress)");

    function _checkNotEmpty(bytes32 value) internal pure returns (bool) {
        if (value == "") {
            return false;
        } else {
            return true;
        }
    }

    /**
     * @dev Resolves the index of a domain name in the Multipass storage.
     * @param domainName The domain name to resolve the index for.
     * @return The index of the domain name in the storage.
     */
    function resolveDomainIndex(bytes32 domainName) internal view returns (uint256) {
        MultipassStorageStruct storage s = MultipassStorage();
        return s.domainNameToIndex[domainName];
    }

    function _getDomainStorage(bytes32 domainName) internal view returns (DomainStorage storage) {
        MultipassStorageStruct storage s = MultipassStorage();

        return s.domains[resolveDomainIndex(domainName)];
    }

    function _initializeDomain(
        address registrar,
        uint256 fee,
        uint256 renewalFee,
        bytes32 domainName,
        uint256 referrerReward,
        uint256 referralDiscount
    ) internal {
        LibMultipass.MultipassStorageStruct storage ms = MultipassStorage();

        uint256 domainIndex = ms.numDomains + 1;
        LibMultipass.DomainStorage storage _domain = ms.domains[domainIndex];
        _domain.properties.registrar = registrar;
        _domain.properties.fee = fee;
        _domain.properties.name = domainName;
        _domain.properties.referrerReward = referrerReward;
        _domain.properties.referralDiscount = referralDiscount;
        _domain.properties.renewalFee = renewalFee;
        ms.numDomains++;
        ms.domainNameToIndex[domainName] = domainIndex;
    }

    function _resolveRecord(NameQuery memory query) private view returns (bool, Record memory) {
        if ((query.wallet == address(0)) && (query.id == bytes32(0)) && (query.name == bytes32(0))) {
            Record memory rv;
            return (false, rv);
        }

        MultipassStorageStruct storage s = MultipassStorage();
        DomainStorage storage _domain = s.domains[s.domainNameToIndex[query.domainName]];
        DomainStorage storage _targetDomain = s.domains[
            s.domainNameToIndex[query.targetDomain == bytes32(0) ? query.domainName : query.targetDomain]
        ];
        address _wallet;
        {
            // resolve wallet
            if (query.wallet != address(0)) {
                _wallet = query.wallet;
            } else if (query.id != bytes32(0)) {
                _wallet = _domain.idToAddress[query.id];
            } else if (query.name != bytes32(0)) {
                bytes32 _id = _domain.nameToId[query.name];
                _wallet = _domain.idToAddress[_id];
            }
        }

        //from wallet find and return record
        return _resolveFromAddress(_wallet, _targetDomain);
    }

    /**
     * @dev Resolves the record of a user.
     * @param query The query to resolve the record for.
     * @return The record of the user.
     * @dev resolves Record of name query in to status and identity
     */
    function resolveRecord(NameQuery memory query) internal view returns (bool, Record memory) {
        return _resolveRecord(query);
    }

    /** @dev this function bears no security checks, it will ignore nonce in arg and will increment
     *   nonce value stored in domain instread
     */
    function _setRecord(DomainStorage storage domain, Record memory record) internal {
        domain.addressToId[record.wallet] = record.id;
        domain.idToAddress[record.id] = record.wallet;
        domain.idToName[record.id] = record.name;
        domain.nameToId[record.name] = record.id;
        domain.nonce[record.id] += 1;
        domain.validUntil[record.wallet] = record.validUntil;
    }

    function _resolveFromAddress(
        address _address,
        DomainStorage storage _domain
    ) private view returns (bool, Record memory) {
        Record memory resolved;

        resolved.id = _domain.addressToId[_address];
        resolved.name = _domain.idToName[resolved.id];
        resolved.nonce = _domain.nonce[resolved.id];
        resolved.wallet = _address;
        resolved.domainName = _domain.properties.name;
        resolved.validUntil = _domain.validUntil[_address];

        if (resolved.id == bytes32(0)) {
            return (false, resolved);
        }
        return (true, resolved);
    }
    /**
     * @dev Resolves the record of a user.
     * @param _record The record to resolve the query for.
     * @return query result.
     */
    function queryFromRecord(Record memory _record) internal pure returns (NameQuery memory) {
        NameQuery memory _query;
        _query.id = _record.id;
        _query.domainName = _record.domainName;
        _query.name = _record.name;
        _query.wallet = _record.wallet;
        return _query;
    }

    function _registerNew(Record memory newRecord, DomainStorage storage domain) internal {
        _setRecord(domain, newRecord);
        domain.properties.registerSize += 1;
    }

    function _getContractState() internal view returns (uint256) {
        LibMultipass.MultipassStorageStruct storage ms = MultipassStorage();
        return ms.numDomains;
    }

    function _getDomainStorageById(uint256 id) internal view returns (DomainStorage storage) {
        MultipassStorageStruct storage s = MultipassStorage();

        return s.domains[id];
    }

    using LibMultipass for NameQuery;
}
","keccak256":"0xdd51454518a914644e35581d83cf93c0aa99810d2ce36c24316e14c577051165","license":"MIT"},"src/UBI.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@peeramid-labs/multipass/src/interfaces/IMultipass.sol";
import "@peeramid-labs/multipass/src/libraries/LibMultipass.sol";
import "./tokens/DistributableGovernanceERC20.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import {LibUBI} from "./libraries/LibUBI.sol";
import {ShortStrings, ShortString} from "@openzeppelin/contracts/utils/ShortStrings.sol";

/**
 * @title Universal Basic Income (UBI) Contract
 * @author Peeramid Labs
 * @notice This contract manages a UBI system where users can claim daily tokens and support proposals.
 * It integrates with Multipass for identity verification and uses an ERC20 token for distribution.
 * Users can claim a fixed amount of tokens daily. After claiming, they can also propose ideas or
 * support existing ones from the previous day using a quadratic voting mechanism.
 * The contract is upgradeable and pausable.
 */
contract UBI is ReentrancyGuardUpgradeable, PausableUpgradeable {
    using ShortStrings for ShortString;
    /**
     * @notice Error thrown when a user without a valid Multipass record tries to interact with the contract.
     * @param recordFound Whether a Multipass record was found for the sender.
     * @param validUtil The timestamp until which the sender's Multipass record is valid.
     */
    error InvalidSender(bool recordFound, uint256 validUtil);

    /**
     * @notice Event emitted when a user successfully claims their daily tokens.
     * @param user The address of the user who claimed.
     * @param amount The amount of tokens claimed.
     */
    event Claimed(address indexed user, uint256 amount);

    /**
     * @notice Event emitted when a proposal is made, indicating how many times it has been proposed.
     * @param proposalHash The hash of the proposal.
     * @param newTimesProposed The new total count of times this proposal has been made.
     */
    event ProposedTime(bytes32 proposalHash, uint256 newTimesProposed);

    /**
     * @notice Event emitted when a user votes on proposals
     * @param participant Address of the voter
     * @param day Day number when the vote was cast
     * @param proposal Hash of the proposal being voted on
     * @param amount Amount of voting power allocated
     */
    event VotingByAddress(address indexed participant, uint256 indexed day, bytes32 indexed proposal, uint256 amount);

    /**
     * @notice Event emitted when a proposal's daily score is updated, indexed by proposer.
     * @param dailyScore The new daily score of the proposal.
     * @param day The day the score was updated.
     * @param proposer The address of the proposal's creator.
     * @param proposal The hash of the proposal.
     */
    event ProposalScoreUpdatedByAddress(
        uint256 indexed dailyScore,
        uint256 indexed day,
        address indexed proposer,
        bytes32 proposal
    );

    /**
     * @notice Event emitted when a proposal's daily score is updated, indexed by proposal hash.
     * @param dailyScore The new daily score of the proposal.
     * @param day The day the score was updated.
     * @param proposal The hash of the proposal.
     * @param proposer The address of the proposal's creator.
     */
    event ProposalScoreUpdatedByProposal(
        uint256 indexed dailyScore,
        uint256 indexed day,
        bytes32 indexed proposal,
        address proposer
    );

    /**
     * @notice Event emitted when a proposal's lifetime statistics are updated.
     * @param lifeTimeScore The new aggregate lifetime score of the proposal.
     * @param proposedTimes The total number of times the proposal has been submitted.
     * @param repostedTimes The total number of times the proposal has been reposted.
     */
    event ProposalLifetimeScore(
        uint256 indexed lifeTimeScore,
        uint256 indexed proposedTimes,
        uint256 indexed repostedTimes
    );

    /**
     * @notice Event for tracking proposals by address
     * @param proposer Address of the proposal creator
     * @param day Day number when the proposal was created
     * @param proposal Hash of the proposal
     * @param proposalText Full text of the proposal
     */
    event ProposingByAddress(
        address indexed proposer,
        uint256 indexed day,
        bytes32 indexed proposal,
        string proposalText,
        uint256 scoreWhenProposed
    );

    /**
     * @notice Event emitted when a user reposts an existing proposal, indexed by reposter.
     * @param proposer The original proposer's address.
     * @param day The day of the repost.
     * @param proposal The hash of the proposal.
     * @param reposter The address of the user who reposted.
     * @param proposalText The text of the proposal.
     */
    event RepostByReposter(
        address indexed proposer,
        uint256 indexed day,
        bytes32 indexed proposal,
        address reposter,
        string proposalText
    );
    /**
     * @notice Event emitted when a user reposts an existing proposal, indexed by original proposer.
     * @param reposter The address of the user who reposted.
     * @param day The day of the repost.
     * @param proposal The hash of the proposal.
     * @param proposer The original proposer's address.
     * @param proposalText The text of the proposal.
     */
    event RepostByProposer(
        address indexed reposter,
        uint256 indexed day,
        bytes32 indexed proposal,
        address proposer,
        string proposalText
    );

    /**
     * @notice Constructor
     * @dev Intentionally empty as initialization happens in initialize()
     */
    constructor(bool isTest) {
        if (!isTest) {
            _disableInitializers();
        }
    }

    /**
     * @notice Initializes the UBI contract.
     * @dev This function is called once to set up the contract's initial state.
     * It sets the Multipass contract instance, the governance token, the pauser address,
     * and the daily claim/support amounts.
     * @param _multipass The address of the Multipass contract.
     * @param _token The address of the DistributableGovernanceERC20 token contract.
     * @param _pauser The address that has permission to pause and unpause the contract.
     * @param dailyClaim The amount of tokens users can claim daily.
     * @param dailySupport The amount of support points users can allocate daily.
     * @param domainName The Multipass domain name for identity verification.
     */
    function initialize(
        IMultipass _multipass,
        DistributableGovernanceERC20 _token,
        address _pauser,
        uint256 dailyClaim,
        uint256 dailySupport,
        bytes32 domainName
    ) public initializer {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        s.multipass = _multipass;
        s.token = _token;
        s.pauser = _pauser;
        s.dailyClaimAmount = dailyClaim;
        s.dailySupportAmount = dailySupport;
        s.domainName = domainName;
        __ReentrancyGuard_init();
        __Pausable_init();
    }

    /**
     * @notice Allows a user to claim their daily tokens and optionally submit a proposal.
     * @dev A user must have a valid Multipass record to call this function. They can only claim once per day.
     * If `data` is provided, it's treated as a proposal. If the proposal is new for the day, it's created.
     * If it's a duplicate of an existing proposal for the day, it's counted as a repost.
     * Claiming also resets the user's daily support allowance.
     * @param data The proposal text. If empty, no proposal is submitted.
     */
    function claim(string memory data) public nonReentrant whenNotPaused {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        LibMultipass.NameQuery memory q = LibMultipass.NameQuery({
            domainName: s.domainName,
            wallet: msg.sender,
            targetDomain: "",
            name: "",
            id: ""
        });
        (bool exists, LibMultipass.Record memory record) = s.multipass.resolveRecord(q);
        require(bytes(data).length <= 1337, "Max Data size 1337, use IPFS link");
        require(exists && record.validUntil > block.timestamp, InvalidSender(exists, record.validUntil));
        uint256 day = currentDay();
        require(s.lastClaimedAt[msg.sender] < day, "Already claimed today");
        s.lastClaimedAt[msg.sender] = day;

        bytes32 hash = keccak256(bytes(data));
        if (s.daily[day].proposals[hash].exists) {
            emit RepostByReposter(msg.sender, day, hash, s.daily[day].proposals[hash].proposer, data);
            emit RepostByProposer(s.daily[day].proposals[hash].proposer, day, hash, msg.sender, data);
            s.proposalGlobalStats[hash].repostedTimes += 1;
        } else {
            if (hash != keccak256("")) {
                s.daily[day].proposals[hash] = LibUBI.DailyProposal({
                    proposal: hash,
                    score: 0,
                    proposer: msg.sender,
                    exists: true
                });
                s.daily[day].proposalCnt++;
                uint256 scoreWhenProposed = s.proposalGlobalStats[hash].aggregateScore;
                s.proposalGlobalStats[hash].proposedTimes += 1;
                emit ProposingByAddress(msg.sender, day, hash, data, scoreWhenProposed);
                emit ProposedTime(hash, s.proposalGlobalStats[hash].proposedTimes);
            }
        }
        s.token.mint(msg.sender, s.dailyClaimAmount);
        emit Claimed(msg.sender, s.dailyClaimAmount);
    }

    /**
     * @notice Pauses all contract functions with the whenNotPaused modifier
     * @dev Can only be called by the WorldMultiSig contract
     */
    function pause() public {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        require(msg.sender == s.pauser, "not a pauser");
        _pause();
    }

    /**
     * @notice Pauses all contract functions with the whenNotPaused modifier
     * @dev Can only be called by the WorldMultiSig contract
     */
    function unpause() public {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        require(msg.sender == s.pauser, "not a pauser");
        _unpause();
    }

    event OnboardingBonusClaimed(address indexed user);

    /**
     * @notice Allows a user to claim their onboarding bonus.
     * @dev The onboarding bonus is a one-time reward for new users.
     * The bonus is claimed by calling this function.
     * The bonus is added to the user's balance.
     * The bonus is only claimable once.
     * @param user The address of the user claiming the bonus.
     */
    function claimOnboardingBonus(address user) public nonReentrant {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        require(s.claimedOnboardingBonus[user] == false, "already claimed");
        LibMultipass.NameQuery memory q = LibMultipass.NameQuery({
            domainName: s.domainName,
            wallet: user,
            targetDomain: "",
            name: "",
            id: ""
        });
        (bool exists, LibMultipass.Record memory record) = s.multipass.resolveRecord(q);
        require(exists && record.validUntil > block.timestamp, InvalidSender(exists, record.validUntil));
        s.claimedOnboardingBonus[user] = true;
        s.token.mint(user, s.dailyClaimAmount);
        emit OnboardingBonusClaimed(user);
    }

    function onboardingBonusClaimed(address user) public view returns (bool) {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        return s.claimedOnboardingBonus[user];
    }

    /**
     * @notice Allows a user to support one or more proposals from the previous day.
     * @dev This function implements a quadratic voting mechanism where the cost to support is the square of the amount.
     * A user must have claimed their tokens for the current day before they can support proposals.
     * The total support spent cannot exceed the `dailySupportAmount`.
     * Users cannot support their own proposals.
     * @param votes An array of `VoteElement` structs, each containing a proposal hash and the amount of support.
     */
    function support(LibUBI.VoteElement[] memory votes) public nonReentrant whenNotPaused {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        LibMultipass.NameQuery memory q = LibMultipass.NameQuery({
            domainName: s.domainName,
            wallet: msg.sender,
            targetDomain: "",
            name: "",
            id: ""
        });
        (bool exists, LibMultipass.Record memory record) = s.multipass.resolveRecord(q);
        require(exists && record.validUntil > block.timestamp, InvalidSender(exists, record.validUntil));
        uint256 day = currentDay();
        for (uint256 i = 0; i < votes.length; i++) {
            LibUBI.VoteElement memory voteElement = votes[i];
            bool proposalExists = s.daily[day - 1].proposals[voteElement.proposal].exists;
            require(proposalExists, "Proposal is not in daily menu :(");
            address proposer = s.daily[day - 1].proposals[voteElement.proposal].proposer;
            require(voteElement.amount < s.dailySupportAmount, "Daily support limit exceeded");
            require(proposer != msg.sender, "Cannot support yourself");
            require(voteElement.amount < 10000, "amount too large");
            s.supportSpent[day][msg.sender] += voteElement.amount * voteElement.amount;
            require(s.supportSpent[day][msg.sender] <= s.dailySupportAmount, "Daily support limit exceeded");
            address user = msg.sender;
            uint256 decimals = s.token.decimals();
            s.token.mint(proposer, voteElement.amount * 10 ** decimals);
            emit VotingByAddress(user, day, voteElement.proposal, voteElement.amount);
            s.proposalGlobalStats[voteElement.proposal].aggregateScore += voteElement.amount;
            s.daily[day - 1].proposals[voteElement.proposal].score += voteElement.amount;
            emit ProposalScoreUpdatedByAddress(
                s.daily[day - 1].proposals[voteElement.proposal].score,
                day,
                proposer,
                voteElement.proposal
            );
            emit ProposalScoreUpdatedByProposal(
                s.daily[day - 1].proposals[voteElement.proposal].score,
                day,
                voteElement.proposal,
                proposer
            );
            emit ProposalLifetimeScore(
                s.proposalGlobalStats[voteElement.proposal].aggregateScore,
                s.proposalGlobalStats[voteElement.proposal].proposedTimes,
                s.proposalGlobalStats[voteElement.proposal].repostedTimes
            );
        }
    }

    /**
     * @notice Calculates the current day based on the block timestamp.
     * @dev The day is calculated as `block.timestamp / 1 days`.
     * @return The current day number.
     */
    function currentDay() public view returns (uint256) {
        return block.timestamp / 1 days;
    }

    /**
     * @notice Gets the total score for a proposal
     * @param proposal Hash of the proposal to query
     * @return uint256 Current score/votes for the proposal
     */
    function proposalLifetimeStats(bytes32 proposal) public view returns (LibUBI.ProposalGlobalStats memory) {
        return LibUBI.getStorage().proposalGlobalStats[proposal];
    }

    /**
     * @notice Gets the address of the pauser.
     * @return The address with pausing/unpausing permissions.
     */
    function pauser() public view returns (address) {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        return s.pauser;
    }

    /**
     * @notice Gets the Multipass contract instance.
     * @return The `IMultipass` contract instance used for identity checks.
     */
    function multipass() public view returns (IMultipass) {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        return s.multipass;
    }

    /**
     * @notice Gets the governance token contract instance.
     * @return The `DistributableGovernanceERC20` token contract used for UBI.
     */
    function token() public view returns (DistributableGovernanceERC20) {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        return s.token;
    }

    /**
     * @notice Retrieves the main parameters of the UBI system.
     * @return dailyClaimAmount The amount of tokens for a daily claim.
     * @return dailySupportAmount The amount of support points available daily.
     * @return domainName The Multipass domain name.
     */
    function getUBIParams()
        public
        view
        returns (uint256 dailyClaimAmount, uint256 dailySupportAmount, bytes32 domainName)
    {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        dailyClaimAmount = s.dailyClaimAmount;
        dailySupportAmount = s.dailySupportAmount;
        domainName = s.domainName;
    }

    /**
     * @notice Gets the details of a proposal for a specific day.
     * @param hash The hash of the proposal.
     * @param day The day to query.
     * @return A `DailyProposal` struct with the proposal's daily information.
     */
    function getProposalDailyScore(bytes32 hash, uint256 day) public view returns (LibUBI.DailyProposal memory) {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        return s.daily[day].proposals[hash];
    }

    /**
     * @notice Gets the total number of unique proposals for a specific day.
     * @param day The day to query.
     * @return The count of proposals for that day.
     */
    function getProposalsCnt(uint256 day) public view returns (uint256) {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        return s.daily[day].proposalCnt;
    }

    /**
     * @notice Gets the day a user last claimed their tokens.
     * @param user The address of the user to query.
     * @return The day number of the last claim. Returns 0 if never claimed.
     */
    function lastClaimedAt(address user) public view returns (uint256) {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        return s.lastClaimedAt[user];
    }

    /**
     * @notice An alias for `currentDay()`.
     * @return The current day number.
     */
    function getCurrentDay() public view returns (uint256) {
        return currentDay();
    }

    /**
     * @notice Gets the current daily state for a user.
     * @param user The address of the user to query.
     * @return claimedToday A boolean indicating if the user has claimed today.
     * @return supportSpent The amount of support points the user has spent today.
     */
    function getUserState(address user) public view returns (bool claimedToday, uint256 supportSpent) {
        LibUBI.UBIStorage storage s = LibUBI.getStorage();
        uint256 day = currentDay();
        claimedToday = s.lastClaimedAt[user] == day ? true : false;
        supportSpent = s.supportSpent[day][user];
    }

    function getShortStringBytes32(string memory text) public pure returns (bytes32) {
        return ShortString.unwrap(ShortStrings.toShortString(text));
    }
}
